INSERT INTO `oiseaux` (`id`, `nom`, `nom_latin`, `ordre:famille`, `categorie`, `couleur`, `description`) VALUES
(1, 'Accenteur alpin', 'Prunella collaris', 'PASSERIFORMES: Prunellidae', 'Passereaux', 'brun', 'Corps brun, tache blanche mouchetée de noir sous le bec. Tête et gorge grises. Flancs légèrement roux, ventre roux. Bec jaune à bout noir.'),
(2, 'Accenteur mouchet', 'Prunella modularis', 'PASSERIFORMES: Prunellidae', 'Passereaux', 'brun', 'Tête et dos bruns, collier et gorge gris, ventre grisâtre aux flancs légèrement striés de brun. Bec noir.'),
(3, 'Agrobate roux', 'Cercotrichas galactotes', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Corps brun roux, ventre pâle. Sourcil blanc, joue blanche, bande noir sur l\'oeil. Longue queue rousse en éventail, tachetées de brun sombre et de blanc sur le bout. Bec orange et noir.'),
(4, 'Aigle botté', 'Hieraaetus pennatus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos brun. Il existe des adultes clairs avec ventre blanc et dessous des ailes blanc bordé de noir, et des adultes foncés entièrement bruns. Bec jaune à bout noir, pattes jaunes.'),
(5, 'Aigle criard', 'Aquila clanga', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Entièrement brun sombre (avec quelques taches plus claires), bec jaune à bout noir, pattes jaunes.'),
(6, 'Aigle de Bonelli', 'Aquila fasciata', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos brun sombre, tête plus claire, ventre blanc moucheté. Vu du dessous, la queue est blanche avec une frange noire.'),
(7, 'Aigle ibérique', 'Aquila adalberti', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos brun sombre avec la tête plus claire. Tache blanche à l\'épaule. Bec noir à base jaune. Pattes jaunes.'),
(8, 'Aigle impérial', 'Aquila heliaca', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos brun sombre, tête plus claire, taches blanches sur les épaules (moindres que pour l\'aigle ibérique). Bec jaune à bout noir, pattes jaunes.'),
(9, 'Aigle pomarin', 'Aquila pomarina', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Entièrement brun sombre. Bec noir à base jaune, pattes jaunes.'),
(10, 'Aigle royal', 'Aquila chrysaetos', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos brun sombre avec la tête plus claire. Vues de dessous les ailes sont noires avec une zone blanche au centre. De même la queue est blanche frangée de noir.'),
(11, 'Aigrette à gorge blanche', 'Egretta gularis', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'blanc', 'Entièrement blanche en phase claire, elle ressemble beaucoup à l\'aigrette garzette, dont elle se distingue par ces pattes (chez la garzette, seuls les doigts sont jaunes, alors que chez l\'aigrette à gorge blanche, la limite entre le noir est le jaune est moins nette). Elle est grise en phase sombre, à l\'exception de la gorge blanche.'),
(12, 'Aigrette garzette', 'Egretta garzetta', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'blanc', 'Echassier entièrement blanc, avec les pattes noires, et les doigts jaunes. Le bec, fin et long, est noir également.'),
(13, 'Alouette calandre', 'Melanocorypha calandra', 'PASSERIFORMES: Alaudidae', 'Passereaux', 'brun', 'Dos brun, ventre blanc, joues brunes surlignées de blanc. Gorge blanche avec une tache noire. Bec conique jaunâtre.'),
(14, 'Alouette calandrelle', 'Calandrella brachydactyla', 'PASSERIFORMES: Alaudidae', 'Passereaux', 'brun', 'Dos brun et ventre blanc. Sourcil blanc. Bec conique brunâtre.'),
(15, 'Alouette des champs', 'Alauda arvensis', 'PASSERIFORMES: Alaudidae', 'Passereaux', 'brun', 'Dos brun, ventre blanc, zone blanche sous le bec, gorge mouchetée de noir et un sourcil blanc plus ou moins prononcé. Huppe pas toujours visible.'),
(16, 'Alouette hausse-col', 'Eremophila alpestris', 'PASSERIFORMES: Alaudidae', 'Passereaux', 'brun', 'Dos gris brun, ventre blanc. Petites cornes noires sur la tête, bande noire au niveau de l\'oeil et de la gorge. Tache jaune plus ou moins marquée sous le bec noir. Pattes noires.'),
(17, 'Alouette lulu', 'Lullula arborea', 'PASSERIFORMES: Alaudidae', 'Passereaux', 'brun', 'Dos brun, ventre blanc, gorge mouchetée. Sourcils blancs qui vont jusqu\'à la nuque. Très légère houppe sur l\'arrière de la tête.'),
(18, 'Arlequin plongeur', 'Histrionicus histrionicus', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Cou, tête et dos gris bleu. Flancs roux. Tache blanche autour de l\'oeil. La femelle est brune, avec une tache blanche derrière l\'oeil.'),
(19, 'Autour des palombes', 'Accipiter gentilis', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'gris', 'Dos gris brun, ventre pâle strié horizontalement, ailes et queue rayés de noir, tête grise avec une calotte sombre et un sourcil blanc. Bec crochu à base jaune et à bout noir. Pattes jaunes.'),
(20, 'Avocette élégante', 'Recurvirostra avosetta', 'CHARADRIIFORMES: Recurvirostridae', 'Limicoles', 'blanc', 'Corps blanc, calotte et nuque noires, taches noires sur les ailes noir. Long bec noir, fin et courbé, longues pattes grises.'),
(21, 'Balbuzard pêcheur', 'Pandion haliaetus', 'ACCIPITRIFORMES: Pandionidae', 'Rapaces', 'brun', 'Dos brun sombre, ventre blanc. Tête blanche avec une bande gris-brun sur l\'œil. Bec crochu noir. Pattes couvertes de plumes blanches, aux serres puissantes.'),
(22, 'Barge à queue noire', 'Limosa limosa', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun gris, tête et cou roux, ventre pâle strié de noir, queue noire. Long bec fin orangé à bout noir, longues pattes noires. Les parties rousses deviennent grises l\'hiver.'),
(23, 'Barge rousse', 'Limosa lapponica', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'rouge/orange', 'Dos brun moucheté de noir (écailles), ventre et tête roux l\'été. Long bec fin et noir, pattes noires. L\'hiver, les parties brunes et rousses virent au gris, et un sourcil blanc apparaît.'),
(24, 'Bec-croisé bifascié', 'Loxia leucoptera', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'rouge/orange', 'Chez le mâle, tête rouge, ventre rouge pâle, dos gris-rouge, ailes noires avec deux bandes blanches qui le distingue des autres bec-croisé. Chez la femelle, les parties rouges sont jaunâtres. Pattes noires et bec crochu noir (les parties supérieure et inférieure du bec sont toutes les deux crochues et se croisent).'),
(25, 'Bec-croisé d\'Écosse', 'Loxia scotica', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'rouge/orange', 'Chez le mâle, tête rouge, ventre rouge pâle, dos gris-rouge, ailes noires. Chez la femelle, les parties rouges sont jaunâtres. Pattes noires et bec crochu noir (les parties supérieure et inférieure du bec sont toutes les deux crochues et se croisent).'),
(26, 'Bec-croisé des sapins', 'Loxia curvirostra', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'rouge/orange', 'Chez le mâle, tête rouge, ventre rouge pâle, dos gris-rouge, ailes noires. Chez la femelle, les parties rouges sont jaunâtres. Pattes noires et bec crochu noir (les parties supérieure et inférieure du bec sont toutes les deux crochues et se croisent). '),
(27, 'Bec-croisé perroquet', 'Loxia pytyopsittacus', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'rouge/orange', 'Chez le mâle, tête rouge, ventre rouge pâle, dos gris-rouge, ailes noires. Chez la femelle, les parties rouges sont jaunâtres. Pattes noires et bec crochu noir (les parties supérieure et inférieure du bec sont toutes les deux crochues et se croisent). Le bec est plus fort que chez les autres bec-croisé.'),
(28, 'Bécasse des bois', 'Scolopax rusticola', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun couleur de feuillage, ventre pâle strié horizontalement. Taches noires sur la tête, long bec fin et noirâtre.'),
(29, 'Bécasseau cocorli', 'Calidris ferruginea', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Tête et ventre couleur de rouille en été, avec un dos moucheté brun roux, noir et blanc. En hiver, dos gris et ventre blanc. Très long bec fin et noir, légèrement courbé, pattes noires.'),
(30, 'Bécasseau de Temminck', 'Calidris temminckii', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos, tête et gorge bruns, ventre blanc. Le dos est légèrement moucheté de noir. En hiver, les parties brunes deviennent grises. Bec fin, long et noir. Pattes jaunes à orangé.'),
(31, 'Bécasseau falcinelle', 'Limicola falcinellus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'En été, dos brun moucheté de noir, calotte brune avec une bande blanche sur l\'oeil. Ventre et gorge pâles. Les parties brunes deviennent grises en hiver. Long bec plus épais que chez les autres bécasseaux, très légèrement courbé au bout.'),
(32, 'Bécasseau maubèche', 'Calidris canutus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'gris', 'En hiver, dos gris et ventre blanc. En été, le ventre et les joues deviennent roux, le dos est gris-brun, tacheté de noir et de blanc (écailles). Long bec fin et noir, pattes noires.'),
(33, 'Bécasseau minute', 'Calidris minuta', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun, moucheté de noir et de blanc (écailles), tête brun noirâtre, léger sourcil blanc. Dos gris en hiver. Long bec fin et noir et pattes noirâtres.'),
(34, 'Bécasseau sanderling', 'Calidris alba', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos, tête et gorge brun roux en plumage nuptial, moucheté de noir et de blanc. Gris l\'hiver, face presque blanche. Long bec fin et noir, pattes noires.'),
(35, 'Bécasseau variable', 'Calidris alpina', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Ventre noir l\'été. Gorge striée. En hiver, pas de tache noire sur le ventre et les parties brunes deviennent grises.'),
(36, 'Bécasseau violet', 'Calidris maritima', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun violacé en été, ventre pâle à la gorge mouchetée. Plus sombre l\'hiver. Bec à base orangée et à bout noir, fin et long.'),
(37, 'Bécassine des marais', 'Gallinago gallinago', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos et tête couleur de feuillage avec des bandes blanches, flancs striés de noir, ventre blanc. Très long bec fin, orangé et noir. Pattes jaunes. En vol nuptial, les ailes produisent une sorte de bêlement.'),
(38, 'Bécassine double', 'Gallinago media', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos et tête couleur de feuillage, avec des bandes crèmes et quelques barres blanches sur les ailes. Ventre blanc aux flancs striés de brun et à la gorge mouchetée. Très long bec fin et noirâtre.'),
(39, 'Bécassine sourde', 'Lymnocryptes minimus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos et tête brunes, couleur de feuillage sombre, avec des bandes crèmes. Ventre blanc, gorge mouchetée. Très long bec fin, à base jaune et à bout noir. Pattes jaunâtres.'),
(40, 'Bergeronnette citrine', 'Motacilla citreola', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'gris', 'Tête et ventre jaunes chez le mâle. Chez la femelle, gorge et ventre jaunes. Pas de jaune chez le jeune. Le dos est gris noir, avec quelques plumes gris clair, les flancs sont gris clair. Bec et pattes noirs. Queue assez longue.'),
(41, 'Bergeronnette des ruisseaux', 'Motacilla cinerea', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'gris', 'Dos et tête gris sombre, bout des ailes noires, sourcil blanc. Ventre et croupion jaunes. Bec noir, pattes brunes. Très longue queue. Sous le bec, bavette noire chez l\'adulte mâle et blanche chez la femelle.'),
(42, 'Bergeronnette grise', 'Motacilla alba', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'gris', 'Calotte et bavette noires, front et joues blanches, dos gris prolongé d\'une longue queue noire. Barre blanche sur les ailes, ventre blanc. Bec et pattes noirs. Les parties noires chez le mâle sont gris sombre chez la femelle.'),
(43, 'Bergeronnette printanière', 'Motacilla flava', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'jaune', 'Dos gris olivâtre, tête grise avec un sourcil blanc bien marqué, ventre entièrement jaune, pattes et bec noirs.'),
(44, 'Bernache à cou roux', 'Branta ruficollis', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Dos et ventre noirs. Calotte et nuque noires. Bande noire sur l\'oeil. Tache blanche entre l\'oeil et le bec ainsi que sur les ailes. Joues rousses. Cou et gorge roux soulignés d\'un collier blanc. Bec et pattes brunâtres.'),
(45, 'Bernache cravant', 'Branta bernicla', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête et cou noirs. Ventre clair ou gris sombre selon les régions. Dos gris. Flancs blancs tigrés. Croupion blanc. Marque blanche au niveau du cou. Bec noir.'),
(46, 'Bernache du Canada', 'Branta canadensis', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête et cou noirs à l\'exception d\'une tache sur le menton. Dos et flancs bruns. Gorge et croupion blancs. Bec noir.'),
(47, 'Bernache nonnette', 'Branta leucopsis', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Joues et front blancs. Dos gris barré de noir. Calotte, cou et gorge noirs. Flancs grisâtres. Bec noir.'),
(48, 'Bihoreau gris', 'Nycticorax nycticorax', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'noir', 'Dos et calotte gris/noirs. Cou et ventre blancs. Ailes grises. Deux longues plumes blanches derrière la tête. Pattes jaunâtres. Oeil rouge. Bec long, épais et noir.'),
(49, 'Blongios nain', 'Ixobrychus minutus', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'brun', 'Calotte, dos et bord des ailes noirs. Ventre blanc. Cou et ventre blanchâtres. Pattes jaunes. Long bec jaune, noirâtre sur le dessus.'),
(50, 'Bondrée apivore', 'Pernis apivorus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos brun. Poitrine mouchetée. Queue dotée de trois barres sombres. Bec crochu à base jaune et à bout noir.'),
(51, 'Bouscarle de Cetti', 'Cettia cetti', 'PASSERIFORMES: Cettiidae', 'Passereaux', 'brun', 'Dos brun. Ventre plus clair. Bec et pattes brunâtres. Sourcil clair, peu marqué.'),
(52, 'Bouvreuil pivoine', 'Pyrrhula pyrrhula', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'gris', 'Calotte noire. Ventre et joue rouges. Queue et bout des ailes noir. Dos gris et croupion blanc. Chez la femelle, les parties rouges sont brunes. Bec épais et noir. '),
(53, 'Bruant cendrillard', 'Emberiza caesia', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'brun', 'Tête et gorge grises. Ventre roux. Dos brun. Bavette rousse. Bec épais et jaune.'),
(54, 'Bruant des neiges', 'Plectrophenax nivalis', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'blanc', 'Dos et queue noirs. Tête et ventre blancs. Bec épais et noir. Marques blanches sur les ailes. En hiver, le plumage est brun et le bec est jaune. '),
(55, 'Bruant des roseaux', 'Emberiza schoeniclus', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'brun', 'Dos brun, rayé de noir. Tête et gorge noires. Moustaches blanches. Ventre blanc. Collier blanc. Bec épais et noir.'),
(56, 'Bruant fou', 'Emberiza cia', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'brun', 'Dos brun. Tête grise, rayée noir. Gorge grise. Ventre roux. Bec épais noirâtre.'),
(57, 'Bruant jaune', 'Emberiza citrinella', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'brun', 'Tête et gorge jaunes. Dos brun jaunâtre. Bec épais noirâtre.'),
(58, 'Bruant lapon', 'Calcarius lapponicus', 'PASSERIFORMES: Calcariidae', 'Passereaux', 'brun', 'Dos brun-noir mêlé de blanc et de brun pâle. En plumage estival, le mâle a la calotte, les joues et la gorge noires, séparées du reste du corps par une bande blanche, la nuque rousse, et le ventre blanc. Bec jaune épais. Les parties noires deviennent brunes en plumage hivernal. La femelle est entièrement brune.'),
(59, 'Bruant mélanocéphale', 'Emberiza melanocephala', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'jaune', 'Dos brun, extrémités des plumes gris sombre. La calotte et les joues sont noires, le ventre, la gorge sont jaunes, avec un collier qui remonte sur la nuque. Bec épais, brunâtre.'),
(60, 'Bruant ortolan', 'Emberiza hortulana', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'brun', 'Dos brun, tête grise, cercle oculaire blanc, ventre roux pâle. Moustache et gorge jaune pâle. Bec orange épais, pattes orange.'),
(61, 'Bruant proyer', 'Emberiza calandra', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'brun', 'Dos brun, poitrine blanche striée verticalement, bec épais jaunâtre.'),
(62, 'Bruant zizi', 'Emberiza cirlus', 'PASSERIFORMES: Emberizidae', 'Passereaux', 'brun', 'Dos brun roux mêlé de noir et de blanc, tête grise avec deux bandes jaunes au-dessus et en-dessous de l\'oeil. Bavette gris noir soulignée par une bande jaune. Ventre jaunâtre strié de brun. Bec épais noir.'),
(63, 'Busard cendré', 'Circus pygargus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'gris', 'Tête, dos et gorge gris. Chez le mâle, bout des ailes noir. Ventre blanc moucheté de brun. Femelle brune. Mâle et femelle ont le croupion blanc. Bec crochu à base jaune et à bout noir. Pattes orange.'),
(64, 'Busard des roseaux', 'Circus aeruginosus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Le mâle a le ventre roux et le dos brun. Ailes grises et brunes à l\'exception du bout noir. Femelle plus sombre avec une tache blanche sous le bec. Bec crochu noirâtre, pattes orange.'),
(65, 'Busard pâle', 'Circus macrourus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'gris', 'Chez le mâle, dos gris, bout des ailes noir, dessous blanc. Femelle brune. Bec crochu à base jaune et à bout noir. Pattes jaunes.'),
(66, 'Busard Saint-Martin', 'Circus cyaneus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'gris', 'Dos et tête grises. Chez le mâle, bout des ailes noir. Bout de la queue barré de noir. Femelle brune. Mâle et femelle ont le croupion blanc. Bec crochu noir. Pattes orange.'),
(67, 'Buse féroce', 'Buteo rufinus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'La coloration des adultes est plus ou moins foncée, mais le dos est brun et le dessous du corps est généralement roux. Queue en éventail. Bec crochu à base jaune et à bout noir.'),
(68, 'Buse pattue', 'Buteo lagopus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Coloration variable selon les adultes. Dessous des ailes blanc. Bout des ailes et de la queue noir. Queue en éventail. Bec crochu noir à base jaune. Pattes couvertes de plumes jusqu\'aux serres jaunes.'),
(69, 'Buse variable', 'Buteo buteo', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos généralement brun foncé. Bout des ailes noir. Il existe des adultes clairs au ventre et au dessous des ailes blancs, et des individus foncés complètement bruns. Queue en éventail. Bec crochu noir à base jaune. Pattes jaunes.'),
(70, 'Butor étoilé', 'Botaurus stellaris', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'brun', 'Corps brun. Dos strié de noir. Calotte noire. Bec jaunâtre, épais et long. Pattes verdâtres.'),
(71, 'Caille des blés', 'Coturnix coturnix', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'brun', 'Dos brun mêlé de noir et de crème, flancs mêlés de blanc, ventre blanc. Grand sourcil crème allant jusqu\'à la nuque. Tache noire sous le menton, gorge blanchâtre, moustache blanche.'),
(72, 'Canard chipeau', 'Anas strepera', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête brune. Corps gris, quelques plumes brunes sur le dos, croupion noir et miroir blanc. Le mâle a le bec noir et la femelle a le bec orangé. Elle est brune avec un miroir blanc et un plumage en écailles.'),
(73, 'Canard colvert', 'Anas platyrhynchos', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête verte, collier blanc, gorge brune. Corps gris avec quelques brunes. Croupion noir avec une plume blanche. Bec jaune, pattes orange. Femelle au bec orange, entièrement brune avec un miroir violet.'),
(74, 'Canard mandarin', 'Aix galericulata', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Le mâle est impossible à confondre en raison de ses couleurs vives (brun, blanc, vert, bleu, reflets violets) et de l\'agencement de ces plumes. La femelle est grise avec les flancs tachetés.'),
(75, 'Canard pilet', 'Anas acuta', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Chez le mâle, tête et nuque brunes, gorge blanche blanc qui se prolonge en une pointe blanche qui remonte entre la joue et la nuque. Flancs gris, dos gris avec des plumes noires bordées de crème. Croupion noir précédé de quelques plumes crème, longue queue noire. La femelle a la tête brune, les flancs brun pâle mêlé de blanc, le dos brun sombre, avec des plumes bordées de roux. Le bec est noir chez les deux.'),
(76, 'Canard siffleur', 'Anas penelope', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Chez le mâle, tête rousse, barrée de jaune pâle sur le dessus. Gorge brun rosé. Dos gris, croupion noir. Femelle brune. Bec gris à bout noir.'),
(77, 'Canard souchet', 'Anas clypeata', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Chez le mâle, tête vert foncé, gorge blanche, flancs roux, croupion et dos noirs mêlés de plumes blanche.. Identifiable à son bec démesuré, noir chez le mâle, orangé chez la femelle, qui a par ailleurs un plumage brun mêlé de noir.'),
(78, 'Cassenoix moucheté', 'Nucifraga caryocatactes', 'PASSERIFORMES: Corvidae', 'Corvidés', 'brun', 'Dos, ventre, gorge et joues brun moucheté de blanc. Queue et bouts des ailes noirs, la queue étant de plus bordée de blanc. Croupion blanc. Epais bec noir. '),
(79, 'Chardonneret élégant', 'Carduelis carduelis', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Dos brun, bout des ailes et queue noirs, barres jaunes sur les ailes. Front et bavette rouge, tour de l\'oeil noir, calotte noire séparée de la zone rouge par une bande blanche. Bec épais rosâtre. Ventre pâle. Chez la femelle, la tête est brune sans tâches de couleur.'),
(80, 'Chevalier aboyeur', 'Tringa nebularia', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'gris', 'Dos gris-brun, ventre blanc, long bec fin et noir.'),
(81, 'Chevalier arlequin', 'Tringa erythropus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'noir', 'Entièrement brun-noir l\'été, dos à écailles, pattes noirâtres. Dos gris et ventre blanc l\'hiver, avec des pattes orange. Long bec fin, noir dessus et à base orange dessous.'),
(82, 'Chevalier cul-blanc', 'Tringa ochropus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos, tête, cou et gorge gris, ventre blanc, sourcil blanc entre l\'oeil et le bec. Le dos est finement moucheté de blanc. La queue est blanche avec des barres noires. Long bec fin grisâtre, pattes de même.'),
(83, 'Chevalier gambette', 'Tringa totanus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos et tête brune, gorge plus pâle densément mouchetée de brun. Pattes oranges, long bec fin, orange à bout noirâtre.'),
(84, 'Chevalier guignette', 'Actitis hypoleucos', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos, tête, cou et gorge brun-gris, long bec fin et noir, pattes verdâtres, ventre blanc. En vol, bande blanche sur les ailes.'),
(85, 'Chevalier stagnatile', 'Tringa stagnatilis', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'gris', 'Dos gris noirâtre l\'été, plus pâle l\'hiver, ventre blanc, long bec fin et noir, très effilé, pattes noires.'),
(86, 'Chevalier sylvain', 'Tringa glareola', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun moucheté de blanc, cou et poitrine blanchâtre striés de brun, ventre blanc, sourcil, bec noirâtre, pattes jaunâtres.'),
(87, 'Chevêche d\'Athéna', 'Athene noctua', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Corps brun moucheté de blanc, avec le ventre plus pâle que le dos. Tête plate, disques faciaux bordés de blanc, bec crochu jaune, œil jaune.'),
(88, 'Chevêchette d\'Europe', 'Glaucidium passerinum', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Dos brun, tête plate brune, ventre blanc strié de brun. Sourcils blancs, disques faciaux peu prononcés, marqués de cercles concentriques incomplets, bec crochu jaune.'),
(89, 'Chocard à bec jaune', 'Pyrrhocorax graculus', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Entièrement noir, à l\'exception du bec jaune et des pattes orange. Souvent en grandes bandes. Oiseau de montagne.'),
(90, 'Choucas des tours', 'Coloeus monedula', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Corps gris noir, particulièrement sombre sur le dessus et au bout des ailes. Nuque gris pâle, front noir. Bec épais noir, pattes noires.'),
(91, 'Chouette de l\'Oural', 'Strix uralensis', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Dos brun très moucheté de blanc, formant une sorte de damier sur les ailes. Ventre blanc strié de brun. Disques faciaux blancs prononcés et ronds, bordés de brun, très prononcés. Yeux noirs, bec crochu jaune. Queue barrée de bandes sombres.'),
(92, 'Chouette de Tengmalm', 'Aegolius funereus', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Dos brun moucheté de blanc, ventre blanc à taches brunes, tête brun sombre, finement mouchetée de blanc, avec un front en V, disques faciaux blancs bordés de brun sombre, oeil jaune cerclé de noir, bec crochu jaune.'),
(93, 'Chouette épervière', 'Surnia ulula', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Dos brun à taches blanhces, ventre blanc couvert de stries brunes horizontales, sauf au niveau du cou. Tête brune finement mouchetée de blanc, formant un V bordé de sourcils blancs. Disques faciaux gris bordés de brun noir. Œil jaune, bec jaune.'),
(94, 'Chouette hulotte', 'Strix aluco', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Corps brun ou gris moucheté de blanc et brun sombre, avec de fines stries verticales noires sur le ventre. Disques faciaux prononcés bordés de brun roux ou grisâtre, séparés par une barre brune, et plus pâles entre les yeux et le bec. Oeil noir, bec jaune.'),
(95, 'Chouette lapone', 'Strix nebulosa', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'gris', 'Corps brun gris mêlé de blanc. Disques faciaux très prononcés et ronds marqués de cercles concentriques. Oeil jaune cerclé de noir, bec jaune.'),
(96, 'Cigogne blanche', 'Ciconia ciconia', 'CICONIIFORMES: Ciconiidae', 'Grands échassiers', 'blanc', 'Entièrement blanche, à l\'exception du bout des ailes noir. Long bec orange, longues pattes orange.'),
(97, 'Cigogne noire', 'Ciconia nigra', 'CICONIIFORMES: Ciconiidae', 'Grands échassiers', 'noir', 'Tête, cou, gorge et dos noirs, ventre blanc, long bec orange et noirâtre, longues pattes orange, tour de l\'oeil orange également.'),
(98, 'Cincle plongeur', 'Cinclus cinclus', 'PASSERIFORMES: Cinclidae', 'Passereaux', 'gris', 'Dos gris, tête et ventre bruns, gorge blanche. Fin bec noir, pattes roses.'),
(99, 'Circaète Jean-le-Blanc', 'Circaetus gallicus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Dos brun, dessous des ailes et corps blancs mouchetés de brun, queue rayée. La tête est un peu plus sombre. Bec crochu gris noir.'),
(100, 'Cisticole des joncs', 'Cisticola juncidis', 'PASSERIFORMES: Cisticolidae', 'Passereaux', 'brun', 'Dos brun avec des plumes noires, ventre pâle, croupion roux (discret). Bord de la queue blanc. Petit bec fin et noirâtre, pattes roses. Tête brune marquée de sourcils crème et de barres plus sombres sur le dessus.'),
(101, 'Cochevis huppé', 'Galerida cristata', 'PASSERIFORMES: Alaudidae', 'Passereaux', 'brun', 'Dos et tête bruns, bavette blanche sous le bec, gorge mouchetée de brun, ventre blanc. Petite huppe sur la tête. Petit bec fin brunâtre, pattes de même.'),
(102, 'Combattant varié', 'Philomachus pugnax', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'En hiver, tête brune, gorge et ventre pâle, dos brun noir aux plumes soulignées de crème. Bec fin, long et noir, pattes verdâtres. En tenue nuptiale, apparition d\'une collerette de couleur variable chez le mâle et d\'une huppe sur la tête. Le bec prend une couleur jaune à rougeâtre. '),
(103, 'Corbeau freux', 'Corvus frugilegus', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Tout noir, à l\'exception d\'une zone de peau nue et blanchâtre à la base du bec. Bec épais noir, pattes noires. Forme des colonies parfois importantes. '),
(104, 'Cormoran huppé', 'Phalacrocorax aristotelis', 'SULIFORMES: Phalacrocoracidae', 'Fous, cormorans, pélicans', 'noir', 'Tout noir (avec des reflets verts), pattes noires, bec noir avec une partie jaune à la base. Pas de tache blanche près du bec, contrairement au grand cormoran, qui est aussi plus grand. Huppe en été uniquement. Il niche uniquement en bord de mer.'),
(105, 'Cormoran pygmée', 'Phalacrocorax pygmaeus', 'SULIFORMES: Phalacrocoracidae', 'Fous, cormorans, pélicans', 'noir', 'Corps noir, tête brune, pattes noires, bec gris noir.'),
(106, 'Corneille mantelée', 'Corvus cornix', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Tête, gorge, bout des ailes et queue noirs, nuque, dos et ventre gris-brun clair. Epais bec noir.'),
(107, 'Corneille noire', 'Corvus corone', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Entièrement noire, du bec (épais) aux pattes.'),
(108, 'Coucou geai', 'Clamator glandarius', 'CUCULIFORMES: Cuculidae', 'Autres', 'noir', 'Dos brun noir, bout des ailes tacheté de blanc. Calotte grise avec une petite huppe. Gorge et ventre blancs. Longue queue à bout blanc. Bec noir assez épais et légèrement crochu.'),
(109, 'Coucou gris', 'Cuculus canorus', 'CUCULIFORMES: Cuculidae', 'Autres', 'gris', 'Dos et tête gris, ventre blanc strié horizontalement de noir. Longue queue mouchetée de blanc. Cercle oculaire jaune. Bec épais et noirâtre. Certaines femelles ont un plumage brun.'),
(110, 'Courlis cendré', 'Numenius arquata', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun moucheté de blanc, ventre blanc densément moucheté de brun, pattes grises, long bec fin et courbé, noir avec une base rougeâtre.'),
(111, 'Courlis corlieu', 'Numenius phaeopus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun moucheté de blanc, ventre blanc densément moucheté de brun, pattes grises, long bec fin et courbé, noir avec une base rougeâtre. Il est plus petit que le courlis cendré et s\'en distingue par des sourcils blancs, et une calotte sombre marquée d\'une bande crème. Le bec est également plus rectiligne à la base, ne s\'incurvant que vers le bout.'),
(112, 'Courvite isabelle', 'Cursorius cursor', 'CHARADRIIFORMES: Glareolidae', 'Limicoles', 'brun', 'Dos brun, ventre blanc. Tête brune, à l\'exception du tache grise à l\'arrière du crane. Fin trait noir sur l\'oeil, surmonté d\'un sourcil blanc, qui s\'étendent jusqu\'à la nuque. Longues pattes grisâtres, bec noir courbé. Dessous des ailes noir.'),
(113, 'Crabier chevelu', 'Ardeola ralloides', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'brun', 'Dos brun roux, tête couverte de stries noires, qui s\'étendent parfois au cou (long lorsqu\'il est déployé), gorge roussâtre . Ventre blanc coupé par une ligne verticale brune. Longues aigrettes blanches et noires derrière la tête. Ailes blanches (en vol). Bec long épais et noirâtre, qui vire au bleu en période nuptiale. Pattes verdâtres à orangé.'),
(114, 'Crave à bec rouge', 'Pyrrhocorax pyrrhocorax', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Tout noir, à l\'exception du bec et des pattes rouges. Le bec est courbé. Le jeune a le bec jaune.'),
(115, 'Cygne chanteur', 'Cygnus cygnus', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'blanc', 'Blanc, bec jaune à la base et noir au bout.'),
(116, 'Cygne siffleur', 'Cygnus columbianus', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'blanc', 'Blanc, bec jaune à la base et noir au bout. La partie jaune est plus réduite que chez le cygne chanteur.'),
(117, 'Cygne tuberculé', 'Cygnus olor', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'blanc', 'Blanc. Renflement noir au-dessus du bec orange.'),
(118, 'Durbec des sapins', 'Pinicola enucleator', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'rouge/orange', 'Ailes et queue noires. La femelle est jaune.'),
(119, 'Échasse blanche', 'Himantopus himantopus', 'CHARADRIIFORMES: Recurvirostridae', 'Limicoles', 'blanc', 'Ailes noires. Marque noire sur la nuque. Calotte noire plus ou moins marquée (parfois absente)'),
(120, 'Effraie des clochers', 'Tyto alba', 'STRIGIFORMES: Tytonidae', 'Chouettes, hiboux', 'brun', 'Face, cou et ventre blancs. Dos brun mêlé de gris, moucheté de blanc et de noir. Disque facial blanc, très marqué, bordé de brun. Quelques plumes brunes autour des yeux noirs. Bec blanchâtre.'),
(121, 'Eider à duvet', 'Somateria mollissima', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'blanc', 'Corps blanc. Flancs et croupion noirs. Tête blanche avec une calotte noire et une nuque verdâtre. Front dans l\'alignement du bec jaunâtre. La femelle est entièrement brune. Canard marin.'),
(122, 'Eider à tête grise', 'Somateria spectabilis', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Tête grise aplatie. Joues verdâtres. Gorge brune. Dos, flancs et croupion noirs avec quelques marques blanches. Bec orange surmonté d\'une excroissance jaune et noire. La femelle est entièrement brune.'),
(123, 'Eider de Steller', 'Polysticta stelleri', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Tête blanche avec une marque verdâtre sur la nuque et au-dessus du bec. Collier noir. Flancs brunâtres marqués d\'un point noir. Dos noir rayé de blanc. Croupion noir.'),
(124, 'Élanion blanc', 'Elanus caeruleus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'blanc', 'Dos gris avec une tache noire sur l\'épaule. Ailes grises à bout noir. Tête blanche avec des yeux rouges. Petit bec crochu et noir. Ventre blanc.'),
(125, 'Engoulevent à collier roux', 'Caprimulgus ruficollis', 'CAPRIMULGIFORMES: Caprimulgidae', 'Autres', 'brun', 'Plumage couleur écorce d\'arbre, brun sombre. Léger collier roux plus ou moins visible. Petit bec noir.'),
(126, 'Engoulevent d\'Europe', 'Caprimulgus europaeus', 'CAPRIMULGIFORMES: Caprimulgidae', 'Autres', 'brun', 'Plumage couleur écorce d\'arbre, brun sombre. Léger collier roux plus ou moins visible. Petit bec noir.'),
(127, 'Épervier à pieds courts', 'Accipiter brevipes', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'gris', 'Chez le mâle, dos gris. Ventre blanc barré horizontalement de brun. La femelle a la dos un peu plus brun que le mâle et son ventre est plus moucheté. Bec crochu et noir, à base jaune. Pattes jaunes.'),
(128, 'Épervier d\'Europe', 'Accipiter nisus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'gris', 'Dos et tête gris ardoise. Ventre blanc barré de roux. Plumes rousses sous le bec et sur les flancs. Bec crochu noir à base jaune. Oeil orange, pattes jaunes. La femelle est plus grosse que le mâle.'),
(129, 'Érismature à tête blanche', 'Oxyura leucocephala', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'rouge/orange', 'Tête blanche avec une tache noire sur le dessus. Cou noir, dos brun, queue noire. Bec enflé à la base, bleu en plumage nuptial.'),
(130, 'Érismature rousse', 'Oxyura jamaicensis', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'rouge/orange', 'Tête noire. Joues blanche. Cou et dos roux. Queue noire, bec enflé à la base, bleu en plumage nuptial.'),
(131, 'Étourneau roselin', 'Pastor roseus', 'PASSERIFORMES: Sturnidae', 'Passereaux', 'noir', 'Dos et ventre brun rosé. Tête, ailes et queue noires. Bec et pattes roses.'),
(132, 'Étourneau sansonnet', 'Sturnus vulgaris', 'PASSERIFORMES: Sturnidae', 'Passereaux', 'noir', 'En plumage nuptial, corps noir avec des reflets violets et bleu vert. Dos et bas ventre moucheté de brun. Bec jaune. En plumage hivernal, globalement plus brun. Ventre et tête densément mouchetés de blanc. Reflets moins visibles, bec noir. Dans les deux cas, les pattes sont rose.'),
(133, 'Étourneau unicolore', 'Sturnus unicolor', 'PASSERIFORMES: Sturnidae', 'Passereaux', 'noir', 'Intégralement noir. Bec jaune, parfois noirâtre.'),
(134, 'Faisan de Colchide', 'Phasianus colchicus', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'rouge/orange', 'Tête bleu vert, barbe rouge autour de l\'oeil. Collier blanc. Corps brun avec des taches couleur crème sur le dos et noires sur le ventre. Longue queue. Bec blanchâtre.'),
(135, 'Faisan de Lady Amherst', 'Chrysolophus amherstiae', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'bleu', 'Gorge verte. Ventre blanc. Ailes partiellement bleu-vert. Tache rouge sur la tête. Croupion jaune et rouge. Plumage à écailles blanc/vert/bleu. Queue très longue. Bec grisâtre. Espèce introduite en Grande-Bretagne.'),
(136, 'Faisan doré', 'Chrysolophus pictus', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'rouge/orange', 'Calotte et croupion jaune. Ventre rouge. Plumes vertes sur le dos. Plumes rouges et bleues sur les flancs. Nuque orange strié de noir. Très longue queue noire et brune. Espèce introduite en Grande-Bretagne.'),
(137, 'Faucon crécerelle', 'Falco tinnunculus', 'FALCONIFORMES: Falconidae', 'Rapaces', 'brun', 'Dos roux tacheté de noir. Ventre clair moucheté. Queue grise finie par un bandeau noir. Tête grise. Bec crochu noir à base jaune. Pattes jaunes.'),
(138, 'Faucon crécerellette', 'Falco naumanni', 'FALCONIFORMES: Falconidae', 'Rapaces', 'brun', 'Dos roux. Ailes grises à bout noir. Ventre roux plus pâle. Tête et queue grises. Bout de la queue noir. Bec crochu noir à base jaune. Pattes jaunes.'),
(139, 'Faucon émerillon', 'Falco columbarius', 'FALCONIFORMES: Falconidae', 'Rapaces', 'gris', 'Chez le mâle, dos gris, dessous et nuque roux tachetés de noir. Chez la femelle, dos brun. Dessous blanc moucheté. Dans les deux cas, bec crochu noir et pattes jaunes. La queue de la femelle est barrée de blanc.'),
(140, 'Faucon gerfaut', 'Falco rusticolus', 'FALCONIFORMES: Falconidae', 'Rapaces', 'gris', 'Dos gris aux plumes bordées de blanc. Dessous blanc moucheté. Bec crochu noirâtre. Pattes blanches. La coloration grise du dos peut être peu marquée.'),
(141, 'Faucon hobereau', 'Falco subbuteo', 'FALCONIFORMES: Falconidae', 'Rapaces', 'noir', 'Tache blanche au niveau du cou. Roux au niveau des pattes et sous la queue chez le mâle. Ventre moucheté. Dos gris. Bec crochu noir à base jaune. Pattes jaunes. Tour des yeux et moustache noirs.'),
(142, 'Faucon kobez', 'Falco vespertinus', 'FALCONIFORMES: Falconidae', 'Rapaces', 'gris', 'Mâle gris sombre exceptée une partie rousse autour des pattes. Ventre de la femelle roux pâle. Chez la femelle, le dessous de la queue est rayé noir et blanc. Les pattes sont orange, et le bec orange avec le bout noir.'),
(143, 'Faucon lanier', 'Falco biarmicus', 'FALCONIFORMES: Falconidae', 'Rapaces', 'brun', 'Calotte rousse. Joues blanches. Ventre moucheté. Dos gris brun. Moustaches noires. Dessous de la queue rayé noir et blanc. Pattes jaunes. Bec crochu noir à base jaune.'),
(144, 'Faucon pèlerin', 'Falco peregrinus', 'FALCONIFORMES: Falconidae', 'Rapaces', 'gris', 'Dos gris. Ventre blanc strié de noir. Larges moustaches noires. Pattes jaunes. Bec noir à base jaune.'),
(145, 'Faucon sacre', 'Falco cherrug', 'FALCONIFORMES: Falconidae', 'Rapaces', 'brun', 'Dos brun. Ventre blanc moucheté de brun. Moustaches noires. Bec noir à base jaune. Pattes jaunes.'),
(146, 'Fauvette à lunettes', 'Sylvia conspicillata', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'brun', 'Tête grise, menton blanc. Ailes légèrement rousses et poitrine rosée. Bec fin noirâtre. Pattes brunes.'),
(147, 'Fauvette à tête noire', 'Sylvia atricapilla', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'gris', 'Calotte noire pour le mâle, rousse pour la femelle. Tête et cou gris. Dos gris brun. Pattes et bec noirs.'),
(148, 'Fauvette babillarde', 'Sylvia curruca', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'brun', 'Gorge et ventre blanc et tête et gris. Pattes et bec noirs.'),
(149, 'Fauvette de Chypre', 'Sylvia melanothorax', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'gris', 'Tête noire. Poitrine tachetée. Menton blanc. Dos gris. Bec noirâtre.'),
(150, 'Fauvette de Rüppell', 'Sylvia rueppelli', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'gris', 'Calotte et gorge noires, joue et nuque grises, moustache blanche, oeil orangé. Dos gris avec quelques plumes noires sur les ailes. Pattes oranges, bec noirâtre. Les zones noires chez le mâle sont mouchetées chez la femelle.'),
(151, 'Fauvette des jardins', 'Sylvia borin', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'brun', 'Tête et dos bruns. Dessous plus pâle. Sourcil pâle peu marqué.'),
(152, 'Fauvette épervière', 'Sylvia nisoria', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'brun', 'Tête noire. Gorge et ventre rayés. Dos gris brun.'),
(153, 'Fauvette grisette', 'Sylvia communis', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'brun', 'Dos gris brun, gorge blanche, tête grise et quelques plumes bordées de roux. Pattes orangées. Bec noir dessus et orangé dessous.'),
(154, 'Fauvette mélanocéphale', 'Sylvia melanocephala', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'gris', 'Dos gris. Ventre gris pâle. Calotte et joues noires, oeil cerclé de rouge, gorge blanche. Petit bec fin noir. Pattes orangées.'),
(155, 'Fauvette orphée', 'Sylvia hortensis', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'brun', 'Calotte et joues noires. Dos gris. Gorge et ventre blancs. Bec fin, noir.'),
(156, 'Fauvette passerinette', 'Sylvia cantillans', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'gris', 'Oeil cerclé de rouge, tête et dos gris, gorge rousse, moustache blanche, ventre pâle. Bec noirâtre. Pattes orangées.'),
(157, 'Fauvette pitchou', 'Sylvia undata', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'gris', 'Oeil cerclé de rouge. Ventre roux chez le mâle. Tête grise, dos gris-brun. Pas de moustache blanche. Bec noirâtre. Pattes orangées.'),
(158, 'Fauvette sarde', 'Sylvia sarda', 'PASSERIFORMES: Sylviidae', 'Passereaux', 'gris', 'Entièrement grise. Oeil rouge. Bec noirâtre. Pattes orangées.'),
(159, 'Flamant rose', 'Phoenicopterus roseus', 'PHOENICOPTERIFORMES: Phoenicopteridae', 'Grands échassiers', 'rose', 'Corps rose pâle. Ailes rose vif et noires. Pattes rose. Bec rose à bout noir.'),
(160, 'Fou de Bassan', 'Morus bassanus', 'SULIFORMES: Sulidae', 'Fous, cormorans, pélicans', 'blanc', 'Entièrement blanc à l\'exception du bout des ailes noir et de la tête légèrement rousse. Yeux bleus. Juvéniles mouchetés de noir. Bec épais et gris, pattes noires.'),
(161, 'Foulque à crête', 'Fulica cristata', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'noir', 'Entièrement noir avec une tache blanche sur le front. Petite crête rouge. Bec blanc. Oeil rouge.'),
(162, 'Foulque macroule', 'Fulica atra', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'noir', 'Entièrement noir avec une tache blanche sur le front. Bec blanc. Oeil rouge.'),
(163, 'Fuligule milouin', 'Aythya ferina', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Dos et flancs gris pâle. Tête brune. Cou et croupion noirs. Femelle entièrement brune. Bec noir avec une tache grise. Oeil rouge.'),
(164, 'Fuligule milouinan', 'Aythya marila', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête et croupion noirs. Dos gris marbré. Flancs blancs. Bec gris. La femelle est d\'un brun très sombre. Elle se distingue de la femelle du morillon par une tache blanche à la base du bec. Oeil jaune.'),
(165, 'Fuligule morillon', 'Aythya fuligula', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Entièrement noir à l\'exception des flancs blancs. Petite huppe chez le mâle. La femelle est d\'un brun très sombre. Bec gris à bout noir. Oeil jaune.'),
(166, 'Fuligule nyroca', 'Aythya nyroca', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Tête et flancs brun roux. Dos plus sombre. Croupion blanc. La femelle est plus terne. Bec gris à bout noir.'),
(167, 'Fulmar boréal', 'Fulmarus glacialis', 'PROCELLARIIFORMES: Procellariidae', 'Mouettes, goéland, fulmars', 'gris', 'Tête et ventre blancs. Dos gris. Bec jaunâtre.'),
(168, 'Gallinule poule-d\'eau', 'Gallinula chloropus', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'noir', 'Entièrement noir, sauf le croupion blanc et une ligne blanche sur le flanc. Bec rouge à bout jaune. Pattes verdâtres.'),
(169, 'Ganga cata', 'Pterocles alchata', 'PTEROCLIDIFORMES: Pteroclidae', 'Autres', 'brun', 'Tête et gorge rousses, soulignées par des bandes noires. Bandes noires au niveau de l\'oeil et du menton. Dos à écailles brunes, jaunes, bleutées et noires. Ventre blanc.'),
(170, 'Ganga unibande', 'Pterocles orientalis', 'PTEROCLIDIFORMES: Pteroclidae', 'Autres', 'brun', 'Tête et gorge gris brun. Tache rousse et noire au niveau du menton. Bande noire sur la poitrine. Bas du ventre noir. Dos moucheté gris brun.'),
(171, 'Garrot à oeil d\'or', 'Bucephala clangula', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Tête noire avec une tache blanche près du bec. Gorge, ventre et flancs blancs, rayés de quelques plumes noires. Dos noir. Bec noir. Pattes oranges. Oeil jaune.'),
(172, 'Garrot d\'Islande', 'Bucephala islandica', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Tête et dos noirs. Gorge et flancs blancs. Grande tache blanche entre l\'oeil et le bec. Ligne de points blancs sur l\'aile. Oeil jaune. Bec noir. Pattes orange.'),
(173, 'Geai des chênes', 'Garrulus glandarius', 'PASSERIFORMES: Corvidae', 'Corvidés', 'brun', 'Tête, dos et ventre bruns. Oeil bleu. Moustaches noires, menton blanc. Bout des ailes et queue noirs. Plumes d\'un bleu brillant sur les ailes. Bec épais et noir.'),
(174, 'Gélinotte des bois', 'Tetrastes bonasia', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'brun', 'Tache noire bordée de blanc au niveau du menton. Sourcils rouges, discrets. Ventre à écailles brunes, noires et blanches. Dos gris brun. Bec noir, court et épais. La femelle n\'a pas de tache noire et est plus terne.'),
(175, 'Glaréole à collier', 'Glareola pratincola', 'CHARADRIIFORMES: Glareolidae', 'Limicoles', 'brun', 'Dos et tête brun-gris. Ventre blanc. Au niveau du menton, tache crème cernée de noir. Petite marque rouge sous le bec. Bec noir.'),
(176, 'Gobemouche à collier', 'Ficedula albicollis', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Dos et tête noirs. Tache blanche au-dessus du bec. Large collier blanc. Ventre blanc. Marques blanches sur les ailes, dont le bout est brun. Bec et pattes noirs.'),
(177, 'Gobemouche à demi-collier', 'Ficedula semitorquata', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Dos et tête noirs. Tache blanche au-dessus du bec. Demi-collier blanc. Ventre blanc. Marques blanches sur les ailes, dont le bout est brun. Bec et pattes noirs.'),
(178, 'Gobemouche gris', 'Muscicapa striata', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'gris', 'Corps brun gris. Ventre plus pâle que le dos. Tête légèrement striée. Bec et pattes noirs. '),
(179, 'Gobemouche nain', 'Ficedula parva', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Dos gris brun. Ventre blanc. Joues et tour de l\'oeil gris. Gorge rousse. Petit bec noir et fin. Pattes noires.'),
(180, 'Gobemouche noir', 'Ficedula hypoleuca', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Corps brun sombre. Tache blanche au-dessus du bec. Ventre et gorge blancs. Bout des ailes blanc.'),
(181, 'Goéland à ailes blanches', 'Larus glaucoides', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'blanc', 'Les jeunes sont brun clair. Les adultes sont blanc crème. Bec noirâtre. Pattes jaunes.'),
(182, 'Goéland argenté', 'Larus argentatus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'gris', 'Tête, nuque et ventre blancs. Dos et ailes gris. Bouts des ailes noirs avec des taches blanches. Bec jaune avec une marque rouge sur la mandibule inférieure. Oeil jaune.'),
(183, 'Goéland bourgmestre', 'Larus hyperboreus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'blanc', 'Corps majoritairement blanc. Ailes gris pâle sans marque noire. Bec jaune avec une tache sur la mandibule inférieure. Pattes roses. Oeil jaune.'),
(184, 'Goéland brun', 'Larus fuscus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'gris', 'Tête et ventre blancs. Ailes et dos gris foncé. Bouts des ailes noirs avec des points blancs. Bec jaune avec une tache rouge sur la mandibule inférieure. Oeil jaune. Diffère du goéland marin par ses pattes jaunes.'),
(185, 'Goéland cendré', 'Larus canus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'gris', 'Tête et ventre blancs. Dos gris. Bout des ailes noir avec des taches blanches. Pas de rouge sur le bec. Pattes jaunes.'),
(186, 'Goéland d\'Audouin', 'Ichthyaetus audouinii', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'gris', 'Tête et ventre blancs. Dos gris. Bout des ailes noir presque sans taches blanches. Bec rouge à bout noirâtre.'),
(187, 'Goéland marin', 'Larus marinus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'noir', 'Tête et ventre blancs. Dos gris noir, bord des ailes blanc. Bec jaune avec une tache rouge sur la mandibule inférieure. Diffère du goéland brun par ses pattes rosâtres et sa grande taille.'),
(188, 'Gorgebleue à miroir', 'Luscinia svecica', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Dos et tête bruns. Ventre grisâtre. Sourcils blancs. Nuque grisâtre. Gorge bleue avec à l\'intérieur une tache blanche ou rouge. Plastron orangé et tache orangée au niveau de la queue. Pas de bleu chez la femelle. Bec noir et pattes noires.'),
(189, 'Grand Corbeau', 'Corvus corax', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Intégralement noir. Bec épais, couvert d\'un duvet noir à la base.'),
(190, 'Grand Cormoran', 'Phalacrocorax carbo', 'SULIFORMES: Phalacrocoracidae', 'Fous, cormorans, pélicans', 'noir', 'Tête et ventre noirs. Dos brun sombre, irisé. Bec épais, blanchâtre, jaune à la base. Tache blanche au niveau de la joue. Oeil vert.'),
(191, 'Grand gravelot', 'Charadrius hiaticula', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'brun', 'Tête et dos bruns. Ventre blanc. Collier noir. Demi collier blanc. Joue et front noirs. Tache blanche sur le front. Bec orange à bout noir. Se distingue du petit gravelot par l\'absence du globe oculaire jaune et la présence d\'une bande alaire blanche visible en vol.'),
(192, 'Grand Harle', 'Mergus merganser', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'blanc', 'Tête noire à reflets verts. Dos noir. Flanc, ventre et cou blancs. Femelle grise à tête rousse. Bec pointu orange. Pattes orange.'),
(193, 'Grand Labbe', 'Stercorarius skua', 'CHARADRIIFORMES: Stercorariidae', 'Labbes', 'brun', 'Corps brun. Bout des ailes noir avec une bande blanche. Tête mouchetée de blanc. Bec légèrement crochu noir. Pattes noires.');
INSERT INTO `oiseaux` (`id`, `nom`, `nom_latin`, `ordre:famille`, `categorie`, `couleur`, `description`) VALUES
(194, 'Grand Tétras', 'Tetrao urogallus', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'noir', 'Cou et tête noirs. Sourcil rouge chez le mâle. Reflets bruns sur le dos et les ailes et bleu-vert sur la gorge. Bec blanchâtre. Femelle brune mouchetée noir, blanc et roux, avec la gorge rousse.'),
(195, 'Grand-duc d\'Europe', 'Bubo bubo', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Dos brun noirâtre. Gorge brune striée de noir. Disque facial gris formant un V au niveau du front. Grandes aigrettes. Oeil orange. Bec noir.'),
(196, 'Grande Aigrette', 'Ardea alba', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'blanc', 'Corps entièrement blanc. Le bec est normalement jaune, mais peut virer de l\'orange au noir en période nuptiale, avec des lores verts à la base. Pattes noires.'),
(197, 'Grande Outarde', 'Otis tarda', 'OTIDIFORMES: Otididae', 'Autres', 'brun', 'Dos brun moucheté de noir. Base du cou et queue rousses. Tête et cou gris. Ventre blanc. Moustache blanche.'),
(198, 'Gravelot à collier interrompu', 'Charadrius alexandrinus', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'brun', 'Dos brun. Calotte brune avec une tache noire à l\'avant. Front blanc. Collier blanc. Demi-collier noir. Ventre blanc. Bec noir.'),
(199, 'Grèbe à cou noir', 'Podiceps nigricollis', 'PODICIPEDIFORMES: Podicipedidae', 'Grèbes', 'noir', 'Tête, cou et dos noirs. Flancs et croupion roux. Quelques plumes crème ébouriffées sur les joues. Oeil rouge. Bec noir. '),
(200, 'Grèbe castagneux', 'Tachybaptus ruficollis', 'PODICIPEDIFORMES: Podicipedidae', 'Grèbes', 'brun', 'Joue et cou roux. Tête et dos d\'un brun plus sombre. Flan et croupion légèrement plus pâles. Tache blanc jaunâtre à la base du bec.'),
(201, 'Grèbe esclavon', 'Podiceps auritus', 'PODICIPEDIFORMES: Podicipedidae', 'Grèbes', 'brun', 'Tête et dos noirs. Cou et flanc bruns. Plumage de la tête volumineux et agrémenté de deux touffes de plumes crème, parfois hérissées. Oeil rouge. Bec noir.'),
(202, 'Grèbe huppé', 'Podiceps cristatus', 'PODICIPEDIFORMES: Podicipedidae', 'Grèbes', 'brun', 'En plumage nuptial : dos gris, cou blanc, joues blanches, arrière de la tête roux, huppe noire. Les plumes de la tête sont érectiles. Oeil rouge. Bec épais rosâtre. Mâle et femelle sont semblables. Les petits sont tigrés. Gris et blanc en hiver.'),
(203, 'Grèbe jougris', 'Podiceps grisegena', 'PODICIPEDIFORMES: Podicipedidae', 'Grèbes', 'gris', 'L\'été, joues blanches, calotte noire et cou brun roux. Dos brun noir. Bec noir avec une tache jaune à la base. Les petits sont tigrés. Gris en hiver.'),
(204, 'Grimpereau des bois', 'Certhia familiaris', 'PASSERIFORMES: Certhiidae', 'Passereaux', 'brun', 'Dos brun moucheté de blanc. Ventre blanc. Bec fin courbé, noirâtre. Grimpe sur les troncs d\'arbres. Très semblable au grimpereau des jardins, dont il se distingue par le chant.'),
(205, 'Grimpereau des jardins', 'Certhia brachydactyla', 'PASSERIFORMES: Certhiidae', 'Passereaux', 'brun', 'Dos brun moucheté de blanc. Ventre blanc. Bec fin courbé, noirâtre. Grimpe sur les troncs d\'arbres. Très semblable au grimpereau des bois, dont il se distingue par le chant.'),
(206, 'Grive draine', 'Turdus viscivorus', 'PASSERIFORMES: Turdidae', 'Passereaux', 'brun', 'Dos brun gris. Dessous blanc, densément moucheté de noir. Bec noirâtre. Pattes rosées. Contrairement à la grive musicienne, les taches pectorales ne sont pas alignées.'),
(207, 'Grive litorne', 'Turdus pilaris', 'PASSERIFORMES: Turdidae', 'Passereaux', 'brun', 'Dos brun. Tête grise. Ventre et croupion blancs. Dessous de l\'aile blanc. Gorge brune mouchetée. Léger sourcil blanc. Cercle oculaire jaune. Bec jaune.'),
(208, 'Grive mauvis', 'Turdus iliacus', 'PASSERIFORMES: Turdidae', 'Passereaux', 'brun', 'Tête et dos bruns. Dessous blanc strié de brun. Flanc et dessous de l\'aile roux. Sourcils blancs.'),
(209, 'Grive musicienne', 'Turdus philomelos', 'PASSERIFORMES: Turdidae', 'Passereaux', 'brun', 'Tête et dos gris brun. Dessous blanc densément moucheté de noir. Bec noir dessus et blanchâtre dessous. Contrairement à la grive draine, les taches pectorales sont alignées.'),
(210, 'Gros-bec casse-noyaux', 'Coccothraustes coccothraustes', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Tête et dos brun roux, dessous plus pâle. Marque blanche sur les ailes noires et sur la queue. Collier gris. Bec épais et court bleu-noir en été, rosâtre l\'hiver. Tâche noire sous ou autour du bec.'),
(211, 'Grue cendrée', 'Grus grus', 'GRUIFORMES: Gruidae', 'Grands échassiers', 'gris', 'Corps gris. Longues pattes noires. Tête et cou noir et blanc. Peau rouge sur le dessus de la tête.'),
(212, 'Grue demoiselle', 'Anthropoides virgo', 'GRUIFORMES: Gruidae', 'Grands échassiers', 'gris', 'Corps gris. Tête et cou noirs. Quelques plumes blanches partent de l\'oeil vers l\'arrière de la tête. Pattes gris noir. Oeil rouge. Bec jaunâtre.'),
(213, 'Guêpier d\'Europe', 'Merops apiaster', 'CORACIIFORMES: Meropidae', 'Autres', 'rouge/orange', 'Dos et calotte roux. Bas du dos jaune. Gorge jaune. Bandeau noir sur l\'oeil. Ventre, bout des ailes et queue bleus. Bec noir et courbé.'),
(214, 'Guifette leucoptère', 'Chlidonias leucopterus', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Tête, ventre et milieu du dos noirs. Dessous des ailes partiellement noir. Dessus des ailes gris. Bec rougeâtre qui paraît plutôt noir. Pattes orange.'),
(215, 'Guifette moustac', 'Chlidonias hybrida', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Corps et ailes grisâtres. Joues blanches. Calotte noire qui s\'estompe l\'hiver. Bec et pattes noires en hiver, rougeâtres le reste de l\'année. Les jeunes ont des ailes mouchetées de bruns et sont dépourvus de calotte noire (elle est grisâtre). Attention à ne pas confondre avec les sternes, plus communes, qui ont le dessous plus clair.'),
(216, 'Guifette noire', 'Chlidonias niger', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Tête et ventre noirs. Dos gris. Le dessous devient blanc en hiver. Bec noir. '),
(217, 'Guillemot à miroir', 'Cepphus grylle', 'CHARADRIIFORMES: Alcidae', 'Pingouins, guillemots, macareux', 'noir', 'Complètement noir avec deux taches blanches symétriques au niveau des ailes. Gris en hiver. Bec noir, pattes rouges.'),
(218, 'Guillemot de Brünnich', 'Uria lomvia', 'CHARADRIIFORMES: Alcidae', 'Pingouins, guillemots, macareux', 'noir', 'Tête, cou et dos noirs. Ventre blanc. Trait blanc au niveau du bec. Bec noir, pattes noires.'),
(219, 'Guillemot de Troïl', 'Uria aalge', 'CHARADRIIFORMES: Alcidae', 'Pingouins, guillemots, macareux', 'noir', 'Tête, cou et dos noirs avec une fine bande blanche sur le bas du dos. Ventre blanc. Certains sujets ont une marque blanche en forme de lunette autour de l\'oeil. Bec noir. Pattes noires.'),
(220, 'Gypaète barbu', 'Gypaetus barbatus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'noir', 'Tête, cou et poitrail roux. Petite barbiche noire et bande noire entre l\'œil et le bec. L\'œil est cerclé de rouge. Front et joues blanchâtres. Dos brun noir. Bec crochu et pattes brunâtres.'),
(221, 'Harelde boréale', 'Clangula hyemalis', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'En été, tête et poitrine noires à l\'exception d\'une tache blanche autour de l\'oeil. Flancs blancs. Dos brun noir. En hiver, les parties noires deviennent blanches à l\'exception de la poitrine et de taches faciales. Le mâle a une très longue queue. La femelle est brune sur le dessus et blanche sur le dessous. Bec noir avec une bande orange.'),
(222, 'Harfang des neiges', 'Bubo scandiacus', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'blanc', 'Mâle entièrement blanc, femelle striée de noir. Vit dans la toundra, plutôt en bord de mer.'),
(223, 'Harle huppé', 'Mergus serrator', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête noire avec reflets verts. Collier blanc. Gorge rousse. Flancs gris. Dos noir et blanc. La femelle est grise avec la tête rousse. Bec orange, long et pointu.'),
(224, 'Harle piette', 'Mergellus albellus', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête et cou blancs délimités par des traits noirs. Tache noire autour de l\'oeil et sur les ailes. La femelle a une calotte brune, les joues blanches, le dos et la gorge gris. Pattes grisâtres. Bec grisâtre.'),
(225, 'Héron cendré', 'Ardea cinerea', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'gris', 'Dos et nuque gris. Gorge et ventre blancs avec quelques stries noires. Joues blanches. Dessus de la tête gris noir. Bec jaune, gris et épais. Quelques aigrettes à l\'arrière de la tête.'),
(226, 'Héron garde-boeufs', 'Bubulcus ibis', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'blanc', 'En période hivernale, le plumage est entièrement blanc. En période nuptiale, plumes orangées sur le dessus de la tête, le ventre et le dos. Bec jaune, long et puissant.'),
(227, 'Héron pourpré', 'Ardea purpurea', 'PELECANIFORMES: Ardeidae', 'Grands échassiers', 'gris', 'Dos gris. Cou roux sur les côtés avec une bande noire, blanc sur le devant avec des stries noires. Le dessus de la tête est noir. Bec noirâtre sur le dessus et jaune en dessous. Bec long et puissant.'),
(228, 'Hibou des marais', 'Asio flammeus', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Dos brun sombre moucheté de blanc. Dessous blanc moucheté de noir. Disque facial prononcé, blanc au centre, brun sur les côté et cerclé de blanc. Bec noir, yeux jaunes. Aigrettes à peine visibles.'),
(229, 'Hibou moyen-duc', 'Asio otus', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'brun', 'Dos gris brun. Devant brun moucheté de blanc. Disque facial coupé par un V frontal gris, bordé de blanc et de noir, brun sur les bords externes. Grandes aigrettes. Yeux jaunes. Bec crochu et noir.'),
(230, 'Hirondelle de fenêtre', 'Delichon urbicum', 'PASSERIFORMES: Hirundinidae', 'Martinets et hirondelles', 'noir', 'Tête, ailes et dos noir (reflets bleus). Gorge et ventre blancs. Croupion blanc caractéristique. Pattes couvertes de plumes blanches. Queue bifide noire, peu échancrée. Bec noir.'),
(231, 'Hirondelle de rivage', 'Riparia riparia', 'PASSERIFORMES: Hirundinidae', 'Martinets et hirondelles', 'brun', 'Calotte, dos et ailes brun-gris. Gorge et ventre blancs. Bande pectorale brune. Queue bifide. Bec brunâtre.'),
(232, 'Hirondelle de rochers', 'Ptyonoprogne rupestris', 'PASSERIFORMES: Hirundinidae', 'Martinets et hirondelles', 'brun', 'Tête et dos brun-gris. Dessous pâle. Taches blanches au niveau de la queue. Bec noir.'),
(233, 'Hirondelle rousseline', 'Cecropis daurica', 'PASSERIFORMES: Hirundinidae', 'Martinets et hirondelles', 'noir', 'Calotte, nuque et croupion roux. Dos et ailes noires à reflets bleus. Gorge et ventre blancs. Queue bifide très échancrée. Bec noir. Espèce méditerranéenne uniquement.'),
(234, 'Hirondelle rustique', 'Hirundo rustica', 'PASSERIFORMES: Hirundinidae', 'Martinets et hirondelles', 'noir', 'Tache rouge au-dessus et au-dessous du bec. Bavette noire. Dos et ailes noires à reflets bleus. Ventre blanc. Tache blanche au niveau de la queue bifide très échancrée. Pattes couvertes de plumes blanches. Bec noir.'),
(235, 'Huîtrier pie', 'Haematopus ostralegus', 'CHARADRIIFORMES: Haematopodidae', 'Limicoles', 'noir', 'Tête, cou, gorge et dos noirs. Ventre blanc. en vol, marques blanches sur les ailes. Bec, pattes et oeil rouge orangé.'),
(236, 'Huppe fasciée', 'Upupa epops', 'CORACIIFORMES: Upupidae', 'Autres', 'rouge/orange', 'Tête et dos bruns. Huppe érectile rousse à taches noires. Les ailes et la queue sont striées de noir et blanc crème. Bec long, pointu et noirâtre. Ventre pâle.'),
(237, 'Hypolaïs des oliviers', 'Hippolais olivetorum', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'gris', 'Dessus gris brun, dessous pâle. Petit bec fin. Pattes grisâtres.'),
(238, 'Hypolaïs ictérine', 'Hippolais icterina', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dessus brun olivâtre, dessous jaune pâle. Bec jaunâtre.'),
(239, 'Hypolaïs pâle', 'Hippolais pallida', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dessus gris brun, dessous pâle. Bec grisâtre.'),
(240, 'Hypolaïs polyglotte', 'Hippolais polyglotta', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dessus brun olivâtre, dessous jaune pâle. Bec jaunâtre.'),
(241, 'Ibis falcinelle', 'Plegadis falcinellus', 'PELECANIFORMES: Threskiornithidae', 'Grands échassiers', 'brun', 'Tête cou, ventre et dos brun roux. Ailes et queue gris noir. Long bec courbé. Niche dans les Balkans.'),
(242, 'Ibis sacré', 'Threskiornis aethiopicus', 'PELECANIFORMES: Threskiornithidae', 'Grands échassiers', 'blanc', 'Tête, cou et bout des ailes noirs. Le reste du plumage est blanc. Long bec courbé et noir. Pattes noires.'),
(243, 'Jaseur boréal', 'Bombycilla garrulus', 'PASSERIFORMES: Bombycillidae', 'Passereaux', 'brun', 'Corps gris-brun. Tache jaune au bout de la queue. Bout des ailes noir avec des taches jaunes, blanches et rouges plus ou moins apparentes selon les individus. Bandeau noir sur l\'oeil. Front et bas du ventre roux. Petite huppe. Bec et pattes noirs.'),
(244, 'Labbe à longue queue', 'Stercorarius longicaudus', 'CHARADRIIFORMES: Stercorariidae', 'Labbes', 'gris', 'Calotte noire, gorge et cou blancs, reste du corps gris, avec une longue queue effilée. Bec légèrement crochu et noir. Pattes noires.'),
(245, 'Labbe parasite', 'Stercorarius parasiticus', 'CHARADRIIFORMES: Stercorariidae', 'Labbes', 'brun', 'En phase claire, calotte noire, collier blanc, corps et ailes gris-brun, dessous pâle avec une bande pectorale noire peu prononcée. Entièrement brun en phase sombre. Longue queue au printemps. Bec et pattes noirs.'),
(246, 'Labbe pomarin', 'Stercorarius pomarinus', 'CHARADRIIFORMES: Stercorariidae', 'Labbes', 'brun', 'Certains adultes entièrement gris-brun. D\'autres avec le ventre et un collier blanc. Bec et pattes noirs.'),
(247, 'Lagopède alpin', 'Lagopus muta', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'blanc', 'Mâle: en hiver, blanc avec le bout de la queue noir et un trait noir entre le bec et l\'oeil. En été le ventre reste blanc et le reste du plumage devient brun avec une crête rouge au-dessus de l\'oeil. La femelle a la même transformation, mais sans la crête au-dessus de l\'oeil.'),
(248, 'Lagopède des saules', 'Lagopus lagopus', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'blanc', 'Adulte mâle : blanc avec le bout de la queue noir l\'hiver, brun-roux et une crête rouge au dessus de l\'oeil l\'été. Femelle : identique au mâle l\'hiver, plumage brun à la belle saison.'),
(249, 'Linotte à bec jaune', 'Carduelis flavirostris', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Dos brun sombre, dessous pâle avec quelques rayures au niveau de la gorge et sur le front. Bec jaune en plumage internuptial et noir en plumage nuptial.'),
(250, 'Linotte mélodieuse', 'Carduelis cannabina', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Dos brun roux, bout des ailes noir, tête brun-gris avec une tache rouge sur le front. Deux grandes taches rougeâtres sur la poitrine, ventre blanchâtre.'),
(251, 'Locustelle fluviatile', 'Locustella fluviatilis', 'PASSERIFORMES: Locustellidae', 'Passereaux', 'brun', 'Dessus brun-gris, dessous blanc, taches brun-olivâtre sur le menton et la poitrine.'),
(252, 'Locustelle luscinioïde', 'Locustella luscinioides', 'PASSERIFORMES: Locustellidae', 'Passereaux', 'brun', 'Dessus brun-gris, dessous blanc. Léger sourcil blanc. Bec fin et grisâtre. Pattes rosées.'),
(253, 'Locustelle tachetée', 'Locustella naevia', 'PASSERIFORMES: Locustellidae', 'Passereaux', 'brun', 'Dos brun avec un motif d\'écailles, dessous pâle. Bec fin, jaune dessous et noirâtre dessus.'),
(254, 'Loriot d\'Europe', 'Oriolus oriolus', 'PASSERIFORMES: Oriolidae', 'Passereaux', 'jaune', 'Mâle à dominante jaune, avec les ailes et une partie de la queue noires. Tache noire près de l\'oeil. Femelle au dos brun, tête et dessous verdâtres. Puissant bec rosâtre.'),
(255, 'Lusciniole à moustaches', 'Acrocephalus melanopogon', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dos brun, ventre blanc. Sourcil blanc, souligné d\'un trait noir sur l\'oeil. Dessus de la tête noir. Bec fin noirâtre.'),
(256, 'Macareux moine', 'Fratercula arctica', 'CHARADRIIFORMES: Alcidae', 'Pingouins, guillemots, macareux', 'noir', 'Joues et ventre blancs. Calotte, collier, dos et ailes noirs. Bec épais aux couleurs vives à dominante orange. Le bec devient plus terne en hiver. Pattes orange.'),
(257, 'Macreuse brune', 'Melanitta fusca', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Entièrement noire à l\'exception quelques taches blanches sous l\'oeil et au bout des ailes. La femelle a la tête partiellement rousse. Bec noir.'),
(258, 'Macreuse noire', 'Melanitta nigra', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'noir', 'Le mâle est entièrement noir, à l\'exception de la partie jaune du bec. La femelle est brune avec une tache blanche sur la joue.'),
(259, 'Marmaronette marbrée', 'Marmaronetta angustirostris', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Dos brun. Gorge et flancs blanchâtres, le tout marbré de taches blanches et noires. Joues blanches, dessus de la tête brun, bec noir.'),
(260, 'Marouette de Baillon', 'Porzana pusilla', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'brun', 'Dos brun avec quelques tâches grises. Sourcils et joues gris. Dessus de la tête brun. Gorge et ventre gris. Flancs et croupions rayés noirs et gris. Pattes verdâtres. Bec noirâtre.'),
(261, 'Marouette ponctuée', 'Porzana porzana', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'brun', 'Corps brun entièrement moucheté de blanc sur le dessous et de blanc et noir sur le dessus. Bec jaune et pattes verdâtres.'),
(262, 'Marouette poussin', 'Porzana parva', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'brun', 'Dos brun, face et ventre gris, bec et pattes verdâtres. Bas du ventre et croupion légèrement moucheté. Pas ou peu de taches gris-blanc sur le dos, pas de rayures grises sur le bas du ventre (contrairement à la marouette de Baillon).'),
(263, 'Martin-pêcheur d\'Europe', 'Alcedo atthis', 'CORACIIFORMES: Alcedinidae', 'Autres', 'bleu', 'Dos bleu, avec des plumes azurées au centre. Tête bleue. Ventre roux. Marques rousses et blanches au niveau de la joue et du cou. Long bec puissant de couleur noirâtre, légèrement orangé sur la mandibule inférieure. Pattes orangées.'),
(264, 'Martinet à ventre blanc', 'Tachymarptis melba', 'APODIFORMES: Apodidae', 'Martinets et hirondelles', 'brun', 'Dos brun-noir, tête noirâtre, ventre blanc avec un collier noir. Occupe des zones montagneuses, des falaises.'),
(265, 'Martinet noir', 'Apus apus', 'APODIFORMES: Apodidae', 'Martinets et hirondelles', 'noir', 'Entièrement noir (avec un dessous parfois plus pâle). Tache blanche sous le bec. Queue bifide. Passe tout son temps en vol. Très fréquent en zone urbaine au printemps et en été.'),
(266, 'Martinet pâle', 'Apus pallidus', 'APODIFORMES: Apodidae', 'Martinets et hirondelles', 'gris', 'Entièrement brun-gris. Ventre à écailles. Tache blanche au bec. Queue légèrement bifide.'),
(267, 'Martinet unicolore', 'Apus unicolor', 'APODIFORMES: Apodidae', 'Martinets et hirondelles', 'noir', 'Entièrement noir, avec une tache blanche sous le bec presque invisible.'),
(268, 'Mergule nain', 'Alle alle', 'CHARADRIIFORMES: Alcidae', 'Pingouins, guillemots, macareux', 'noir', 'Tête, cou et dos noirs, ventre blanc. Bec noir légèrement crochu. Le plus petit des alcidés. Vit dans les zones arctiques (nord de la Scandinavie, Groenland), mais peut hiverner sur les côtes atlantiques françaises.'),
(269, 'Merle à plastron', 'Turdus torquatus', 'PASSERIFORMES: Turdidae', 'Passereaux', 'noir', 'Brun très sombre, avec une bande pectorale blanche. Les plumes des ailes et du ventre présentent un liseré plus clair, formant un motif d\'écailles. Bec jaunâtre et pattes noires.'),
(270, 'Merle noir', 'Turdus merula', 'PASSERIFORMES: Turdidae', 'Passereaux', 'noir', 'Mâle adulte noir, avec le bec jaune orangé, et le tour de l\'oeil jaune. La femelle est brune, son bec est souvent noirâtre à la base. Chez le jeune, le corps et le bec sont plutôt bruns.'),
(271, 'Mésange à longue queue', 'Aegithalos caudatus', 'PASSERIFORMES: Aegithalidae', 'Passereaux', 'brun', 'Dos brun et noir. Ventre d\'un brun très pâle. Tête noire avec une raie blanche au milieu. Joues blanches. Longue queue noire bordée de blanc. Quelques plumes blanches sur les ailes noires. Petit bec noir.'),
(272, 'Mésange bleue', 'Cyanistes caeruleus', 'PASSERIFORMES: Paridae', 'Passereaux', 'bleu', 'Calotte, nuque, queue et bout des ailes bleus. Dos verdâtre. Ventre jaune. Fine barre noire sur le ventre et l\'oeil. Joues blanches. Petit bec et pattes noirâtres.'),
(273, 'Mésange boréale', 'Poecile montanus', 'PASSERIFORMES: Paridae', 'Passereaux', 'brun', 'Dos brun, ventre pâle, calotte noire, bavette noire plus ou moins étendue, joues blanches. Petit bec noir. Ressemble beaucoup à la mésange nonnette.'),
(274, 'Mésange charbonnière', 'Parus major', 'PASSERIFORMES: Paridae', 'Passereaux', 'jaune', 'Dos verdâtre, bout des ailes bleuté, queue noire. Joues blanches. Calotte, collier et gorge noirs. Ventre jaune divisé par une large bande noire. Petit bec noir.'),
(275, 'Mésange huppée', 'Lophophanes cristatus', 'PASSERIFORMES: Paridae', 'Passereaux', 'brun', 'Dos brun-gris, ventre pâle. Petite bavette noire. Tête et huppe grises. Collier blanc et noir. Petit bec noir.'),
(276, 'Mésange lapone', 'Poecile cinctus', 'PASSERIFORMES: Paridae', 'Passereaux', 'brun', 'Dos brun, ventre pâle aux flancs roux. Calotte et bavette noires, bout des ailes noir. Joues blanches. Petit bec noir.'),
(277, 'Mésange lugubre', 'Poecile lugubris', 'PASSERIFORMES: Paridae', 'Passereaux', 'gris', 'Calotte et gorge noires, joues blanches, dos gris, dessous blanchâtre. La calotte de la femelle est moins sombre et contrastée. Petit bec noir.'),
(278, 'Mésange noire', 'Periparus ater', 'PASSERIFORMES: Paridae', 'Passereaux', 'gris', 'Tête noire avec des joues blanches et une tache blanche sur la nuque. Le dos et les ailes sont gris foncé, avec des deux barres blanches sur le bord des ailes. Dessous pâle. Petit bec noir.'),
(279, 'Mésange nonnette', 'Poecile palustris', 'PASSERIFORMES: Paridae', 'Passereaux', 'brun', 'Dos brun, ventre pâle, calotte noire, petite bavette noire, joues blanches. Ressemble beaucoup à la mésange boréale, mais la bavette est moins étendue. La queue est également moins étagée. Petit bec noir.'),
(280, 'Mésangeai imitateur', 'Perisoreus infaustus', 'PASSERIFORMES: Corvidae', 'Corvidés', 'brun', 'Dos gris-brun, bout des ailes grises, tête brun sombre, cou et gorge d\'un brun plus terne, ventre roussâtre. Quelques plumes d\'un roux plus éclatant sur l\'aile et la queue. Petit bec noir. Scandinavie.'),
(281, 'Milan noir', 'Milvus migrans', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Corps brun sombre, avec le bout des ailes noires. Tête d\'un brun grisâtre, avec un bec jaune et noir. Vus du dessous, le bord des ailes et la queue sont grisâtres légèrement rayés. La queue est un peu incurvée.'),
(282, 'Milan royal', 'Milvus milvus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Corps brun roux, tête grisâtre, bec jaune et noir. Vues de dessous, les ailes sont brunes près du corps, blanches au centre et noires sur les extrémités. Le dessous de la queue est plus aussi blanchâtre, finement rayé de noir. La queue est échancrée.'),
(283, 'Moineau domestique', 'Passer domesticus', 'PASSERIFORMES: Passeridae', 'Passereaux', 'brun', 'Chez le mâle, dos brun roussâtre, joues, calotte et ventre gris, bandes rousses sur l\'arrière de la tête. Tache noire près de l\'œil, bavette noire qui s\'efface en plumage internuptial dont la femelle est dépourvue. Bec épais.'),
(284, 'Moineau espagnol', 'Passer hispaniolensis', 'PASSERIFORMES: Passeridae', 'Passereaux', 'brun', 'Chez le mâle, dos brun avec des taches noires, calotte rousse, joues blanches, bavette noire, tache noire près de l\'oeil. La bavette noire s\'efface en plumage internuptial, et la femelle en est dépourvue. Taches noires sur la poitrine pâle. Bec épais.'),
(285, 'Moineau friquet', 'Passer montanus', 'PASSERIFORMES: Passeridae', 'Passereaux', 'brun', 'Ventre blanc, dos brun, calotte brune (et non rousse), tâche noire près de l\'oeil, bavette noire. Point noir dans la joue blanche qui le distingue des autres moineaux. La gorge noire s\'efface en plumage internuptial, et la femelle en est dépourvue. Bec épais noir.'),
(286, 'Moineau soulcie', 'Petronia petronia', 'PASSERIFORMES: Passeridae', 'Passereaux', 'brun', 'Dos brun pâle. Sourcil blanc et collier blanc peu marqué. Petite tache jaune au niveau de la gorge. Ventre rayé. Bec épais.'),
(287, 'Monticole bleu', 'Monticola solitarius', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'bleu', 'Entièrement bleu sombre, avec les ailes et la queue noirâtres. La femelle est brune. Bec noir. Pattes noires.'),
(288, 'Monticole de roche', 'Monticola saxatilis', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Dos noir. Ventre et gorge roux. Tête et cou bleu sombre. Bord de la queue orange. La femelle est brune. Bec fin noir.'),
(289, 'Mouette mélanocéphale', 'Ichthyaetus melanocephalus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'blanc', 'En été, tête noire, avec un capuchon plus étendu que celui de la mouette rieuse. Et contrairement à cette dernière, le bout de l\'aile blanc. En hiver, la tache noire se réduit à un point noir et quelques marques grises. Bec et pattes orange.'),
(290, 'Mouette pygmée', 'Hydrocoloeus minutus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'gris', 'Ailes gris clair sur le dessus, plus sombres en dessous. Capuchon noire, similaire à celui de la mouette rieuse, réduite à des taches grises l\'hiver. C\'est la plus petite des mouettes. Scandinavie en été. Peut hiverner en Méditerranée.'),
(291, 'Mouette rieuse', 'Chroicocephalus ridibundus', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'blanc', 'En été, tête d\'un brun très foncé. Le capuchon est plus réduit que pour la mouette mélanocéphale. Frange noire sur le bout de l\'aile. En hiver, tête blanche à l\'exception d\'un point noir à côté de l\'oeil. Bec rouge sombre.'),
(292, 'Mouette tridactyle', 'Rissa tridactyla', 'CHARADRIIFORMES: Laridae', 'Mouettes, goéland, fulmars', 'gris', 'Tête et ventre blancs. Dos gris. Bout des ailes entièrement noir. Bec jaune.'),
(293, 'Nette rousse', 'Netta rufina', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Chez le mâle, dos brun, gorge, nuque et croupion noirs, tête rousse au plumage ébouriffé, flancs blancs, bec rouge. Femelle brune avec les joues blanches et un bec noirâtre.'),
(294, 'Niverolle alpine', 'Montifringilla nivalis', 'PASSERIFORMES: Passeridae', 'Passereaux', 'gris', 'Dos brun, tête grise. Bout des ailes noir, avec une barre alaire blanche. Bec jaune en période internuptiale, noir le reste du temps.'),
(295, 'Océanite cul-blanc', 'Oceanodroma leucorhoa', 'PROCELLARIIFORMES: Hydrobatidae', 'Mouettes, goéland, fulmars', 'noir', 'Entièrement noir, à l\'exception du croupion blanc.'),
(296, 'Océanite frégate', 'Pelagodroma marina', 'PROCELLARIIFORMES: Hydrobatidae', 'Mouettes, goéland, fulmars', 'noir', 'Dos brun noirâtre, bout des ailes et de la queue noir, ventre et dessous des ailes blancs. Joues blanches avec une tâche noire autour de l\'oeil. Bec et pattes noires.'),
(297, 'Océanite tempête', 'Hydrobates pelagicus', 'PROCELLARIIFORMES: Hydrobatidae', 'Mouettes, goéland, fulmars', 'noir', 'Entièrement noir, à l\'exception du croupion blanc.'),
(298, 'Oedicnème criard', 'Burhinus oedicnemus', 'CHARADRIIFORMES: Burhinidae', 'Limicoles', 'brun', 'Dos brun, bout des ailes noirs, gorge pâle striée de noir. Sourcils et moustache blancs. Grandes pattes jaunes, bec jaune à la base et noir à l\'extrémité.'),
(299, 'Oie à bec court', 'Anser brachyrhynchus', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Très semblable à l\'oie des moissons (parfois considérée comme une sous-espèce). Dos brun-gris, tête et ventre bruns, croupion blanc, bec noir et rose, pattes roses. Espèce migratrice : passe les étés dans les toundras arctiques.'),
(300, 'Oie cendrée', 'Anser anser', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Corps gris-brun, bec orange, pattes roses, croupion blanc'),
(301, 'Oie des moissons', 'Anser fabalis', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Très semblable à l\'oie à bec court. Dos brun-gris, tête et ventre bruns, croupion blanc, bec noir et orange, pattes orange.'),
(302, 'Oie naine', 'Anser erythropus', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Entièrement brun sombre sauf le croupion blanc et la tache blanche au-dessus du bec rose.'),
(303, 'Oie rieuse', 'Anser albifrons', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Tête, cou et dos brun-gris, tache blanche au-dessus du bec rose, barres noires sur le ventre pâle, pattes orangées.'),
(304, 'Ouette d\'Égypte', 'Alopochen aegyptiaca', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Tête brun clair avec une tache plus sombre autour de l\'oeil. Ventre blanchâtre, avec une tache brune, dos brun. Ailes blanches et noires avec quelques plumes vertes.'),
(305, 'Outarde canepetière', 'Tetrax tetrax', 'OTIDIFORMES: Otididae', 'Autres', 'brun', 'Dos brun, ventre blanc, tête grise à calotte brune. Collier noir et blanc complexe.'),
(306, 'Panure à moustaches', 'Panurus biarmicus', 'PASSERIFORMES: Panuridae', 'Passereaux', 'brun', 'Dos brun-roux, ailes rousses bordées de plumes blanchâtres et noires, gorge blanche. Chez le mâle, le dessus de la tête est gris, avec des moustaches noires qui descendent depuis l\'oeil. Bec jaune et épais. Pattes noires. Longue queue. La femelle a la tête brune et n\'a pas de moustaches.'),
(307, 'Pélican blanc', 'Pelecanus onocrotalus', 'PELECANIFORMES: Pelecanidae', 'Fous, cormorans, pélicans', 'blanc', 'Entièrement blanc, à l\'exception de quelques plumes noires visibles en vol au bord des ailes. Huppe blanche sur la tête. Grande poche jaune sous le bec. Pattes roses à rougeâtres. Europe de l\'est.'),
(308, 'Pélican frisé', 'Pelecanus crispus', 'PELECANIFORMES: Pelecanidae', 'Fous, cormorans, pélicans', 'blanc', 'Plumage d\'un blanc grisâtre, huppe sur la tête. La poche sous le bec est rouge orangée. Balkans.'),
(309, 'Perdrix bartavelle', 'Alectoris graeca', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'gris', 'Dos gris, flancs rayés de brun noir, joues et gorge blanches cernées de noir. Difficile à distinguer de la perdrix choukar. Alpes, Balkans.'),
(310, 'Perdrix choukar', 'Alectoris chukar', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'gris', 'Dos gris, flancs rayés de noir, joues et gorge blanches cernées de noir. Ventre brun. Difficile à distinguer de la perdrix bartavelle. Balkans.'),
(311, 'Perdrix gambra', 'Alectoris barbara', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'brun', 'Dos gris, flancs barrés de noir et de brun. Calotte brune, joues et gorge grises. Demi collier brun à taches blanches. Bec et pattes rouge, cercle oculaire rouge. Seulement dans le sud de l\'Espagne et en Sardaigne.'),
(312, 'Perdrix grise', 'Perdix perdix', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'gris', 'Dos, tête et gorge gris, face rousse. Tache rouille sur le ventre chez le mâle. Flancs barrés de brun.'),
(313, 'Perdrix rouge', 'Alectoris rufa', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'brun', 'Dos et dessus de la tête brun. Sourcil blanc souligné par une bande noire qui descend le long de la gorge mouchetée noir et blanc. Ventre brun et gris, flancs barrés de brun. Bec et pattes rouge, cercle oculaire rouge.'),
(314, 'Petit gravelot', 'Charadrius dubius', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'brun', 'Dos brun, ventre blanc, calotte brune, masque facial noir et blanc, collier noir et blanc. Bec fin noirâtre. Se distingue du grand gravelot par son cercle oculaire jaune, son bec entièrement noir et l\'absence de barre alaire blanche visible en vol.'),
(315, 'Petit Pingouin', 'Alca torda', 'CHARADRIIFORMES: Alcidae', 'Pingouins, guillemots, macareux', 'noir', 'Dos et tête noirs, ventre blanc. Barre alaire blanche. Le bec noir est marqué d\'une fine ligne blanche. Ligne blanche entre le bec et l\'oeil.'),
(316, 'Petit-duc scops', 'Otus scops', 'STRIGIFORMES: Strigidae', 'Chouettes, hiboux', 'gris', 'Entièrement gris-brun, avec un plumage proche couleur d\'écorce. Petites aigrettes pas toujours visibles. Bec noir, et yeux jaunes.'),
(317, 'Phalarope à bec étroit', 'Phalaropus lobatus', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos gris, calotte et joues grises. En période nuptiale, marque rouge au niveau du cou. Gorge grise. Ventre blanc. Tache blanche au niveau du menton. Long bec étroit et noir. Petite tache blanche au-dessus de l\'oeil. En hiver, la partie rousse devient blanche.'),
(318, 'Phalarope à bec large', 'Phalaropus fulicarius', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'rouge/orange', 'Dos moucheté brun et noir, ventre et gorge roux, calotte brun-noir, joues blanches. En hiver, dos gris, tête et cou blancs, avec une virgule noire au niveau de l\'oeil. Bec jaunâtre à bout noir en été et noir en hiver.'),
(319, 'Phragmite aquatique', 'Acrocephalus paludicola', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dos brun avec quelques plumes plus sombres. Sourcil clair. Dessus de la tête rayé noir et blanchâtre. Ventre blanc. Bec fin.'),
(320, 'Phragmite des joncs', 'Acrocephalus schoenobaenus', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dos brun, bout des ailes noirâtres, bavette blanche, ventre blanchâtre. Sourcil clair. Calotte striée. Petit bec noirâtre.'),
(321, 'Pic à dos blanc', 'Dendrocopos leucotos', 'PICIFORMES: Picidae', 'Pics', 'noir', 'Calotte rouge pour le mâle, noire pour la femelle, joues blanches soulignées de noir. Dos noir avec une vaste tâche blanche, ailes striées de blanc et de noir. Ventre blanc légèrement rayé. Bas du ventre roux.'),
(322, 'Pic cendré', 'Picus canus', 'PICIFORMES: Picidae', 'Pics', 'vert', 'Dos verdâtre, croupion jaune, tête grise, avec une moustache noire et une petite tache noir près de l\'oeil. Chez le mâle, tache rouge sur le front, plus petite que celle du pic vert. Ventre grisâtre.'),
(323, 'Pic épeiche', 'Dendrocopos major', 'PICIFORMES: Picidae', 'Pics', 'noir', 'Dos noir, grande marque blanche sur les ailes, calotte noire avec une tache rouge sur l\'arrière de la tête seulement. Tâche brunâtre juste au-dessus du bec. Joues et ventre blancs. Collier noir. Bas du ventre rouge.'),
(324, 'Pic épeichette', 'Dendrocopos minor', 'PICIFORMES: Picidae', 'Pics', 'noir', 'Dos et ailes rayés noir et blanc, calotte rouge et arrière de la tête noir chez le mâle, calotte noire chez la femelle. Joues blanches. Ventre blanc avec quelques fines rayures noires sur la gorge. Tout petit pic.'),
(325, 'Pic mar', 'Dendrocopos medius', 'PICIFORMES: Picidae', 'Pics', 'noir', 'Dos noir, ailes rayées blanches et noir sur le bout, avec une grande tache blanche plus haut. Calotte rouge, joues blanches. Le trait noir qui les délimite s\'arrête bien avant le bec. Plumes rouges à l\'extrême bas du ventre. Ventre blanchâtre finement rayé de noir. '),
(326, 'Pic noir', 'Dryocopus martius', 'PICIFORMES: Picidae', 'Pics', 'noir', 'Entièrement noir avec une calotte rouge.'),
(327, 'Pic syriaque', 'Dendrocopos syriacus', 'PICIFORMES: Picidae', 'Pics', 'noir', 'Dos noir, grande marque blanche sur les ailes, calotte noire avec une tache rouge sur l\'arrière de la tête seulement. Tâche brunâtre juste au-dessus du bec. Joues et ventre blancs. Bas du ventre rouge. Ressemble beaucoup au pic épeiche, dont il se distingue par une plus grande surface blanche au niveau du cou. Essentiellement présent dans l\'Europe du sud-est.'),
(328, 'Pic tridactyle', 'Picoides tridactylus', 'PICIFORMES: Picidae', 'Pics', 'noir', 'Dos noir coupé verticalement par une large bande blache. Calotte jaune chez le mâle, grise chez la femelle. Tête tigrée de noir et blanc (les bandes blanches sont parfois presque absentes). Ventre grisâtre moucheté. Espèce montagnarde. En France, uniquement dans les Alpes.'),
(329, 'Pic vert', 'Picus viridis', 'PICIFORMES: Picidae', 'Pics', 'vert', 'Dos verdâtre, croupion jaune vif. Calotte rouge. Tache noir autour de l\'oeil. Ventre pâle. Moustache rouge chez le mâle.'),
(330, 'Pie bavarde', 'Pica pica', 'PASSERIFORMES: Corvidae', 'Corvidés', 'noir', 'Tête et dos noirs, reflets bleus sur les ailes et la longue queue. Ventre blanc. Marques blanches sur les ailes. Bec et pattes noires.'),
(331, 'Pie bleue', 'Cyanopica cyanus', 'PASSERIFORMES: Corvidae', 'Corvidés', 'brun', 'Corps brun gris, dessous brun pale, calotte noire, bavette blanchâtre. Ailes et longue queue bleues. Bec et pattes noires.'),
(332, 'Pie-grièche à poitrine rose', 'Lanius minor', 'PASSERIFORMES: Laniidae', 'Passereaux', 'brun', 'Dos gris, dessous légèrement rosé. Bande noire sur l\'oeil, joues blanches. Ailes noires avec une petite marque blanche. Bec épais, jaunâtre à la base et noir au bout.'),
(333, 'Pie-grièche à tête rousse', 'Lanius senator', 'PASSERIFORMES: Laniidae', 'Passereaux', 'noir', 'Dos noir avec quelques plumes blanches, ventre et joues blancs, calotte et nuque rousses, bande noire sur l\'oeil. Bec épais et noir. Pattes noires.'),
(334, 'Pie-grièche écorcheur', 'Lanius collurio', 'PASSERIFORMES: Laniidae', 'Passereaux', 'brun', 'Dos brun roux, tête et croupion gris, ventre blanchâtre. Queue noire sur le dessus. Bande noire sur l\'oeil. Bec grisâtre, épais et crochu.'),
(335, 'Pie-grièche grise', 'Lanius excubitor', 'PASSERIFORMES: Laniidae', 'Passereaux', 'gris', 'Dos et tête gris clair. Aile et queue noires, avec des taches blanches sur les ailes. Bandeau noir sur l\'oeil. Bec noirâtre, épais et crochu.'),
(336, 'Pie-grièche masquée', 'Lanius nubicus', 'PASSERIFORMES: Laniidae', 'Passereaux', 'noir', 'Dos noir, tête noire, bande noire sur l\'oeil. Ailes noires avec de grandes taches blanches. Front et bavette blancs. Ventre roux. Bec noir épais et crochu.'),
(337, 'Pigeon biset', 'Columba livia', 'COLUMBIFORMES: Columbidae', 'Pigeons et tourterelles', 'gris', 'Globalement gris. Gris plus sombre pour la tête que pour le dos. Tache blanche au-dessus du bec. Barres noires sur les ailes. Reflet vert au niveau du cou. Croupion blanc. Les pigeons qu\'on voit fréquemment en ville sont des croisements de pigeons bisets et domestiques.'),
(338, 'Pigeon colombin', 'Columba oenas', 'COLUMBIFORMES: Columbidae', 'Pigeons et tourterelles', 'gris', 'Plumage d\'un gris uniforme, petites barres noires sur les ailes (bien plus réduites que chez le pigeon biset), tache blanche au-dessus du bec, reflets verts sur la nuque et bordeaux sur la gorge.'),
(339, 'Pigeon ramier', 'Columba palumbus', 'COLUMBIFORMES: Columbidae', 'Pigeons et tourterelles', 'gris', 'Corps gris, reflets verdâtres au niveau du cou, gorge rosée, tache blanche sur le cou. Marques blanches sur les ailes visibles en vol. Bec rouge orangé.'),
(340, 'Pinson des arbres', 'Fringilla coelebs', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Le mâle est globalement brun roux, plus pâle sur le dessous, avec une calotte et une nuque grise. Bout des ailes noires à taches blanches. Croupion verdâtre (peu visible). Queue grise et noire. Epais bec noir. La femelle est d\'un brun plus terne, sans marques grises.'),
(341, 'Pinson du Nord', 'Fringilla montifringilla', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Plumage mêlé brun et noir, avec un motif d\'écailles sur le dos, des plumes noires bordées de brun sur les ailes. Tête noirâtre, poitrine rousse. Légèrement moucheté de noir sur le bas du ventre. Croupion blanc. Epais bec jaune à bout noirâtre.'),
(342, 'Pipit à gorge rousse', 'Anthus cervinus', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'brun', 'Dos brun et noir, tête et gorge légèrement rousses en plumage nuptial, ventre moucheté de manière plus ou moins marquée. Bec fin jaune noirâtre.'),
(343, 'Pipit des arbres', 'Anthus trivialis', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'brun', 'Dos brun tacheté, sourcil blanc esquissé, poitrine moucheté. Bec fin.'),
(344, 'Pipit farlouse', 'Anthus pratensis', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'brun', 'Dos brun barré de noir. Bout des ailes et queue noirs, bordés de brun. Pas de sourcil blanc, contrairement à la majorité des pipits, cercle oculaire blanc. Gorge blanche mouchetée. Bec fin jaunâtre.'),
(345, 'Pipit maritime', 'Anthus petrosus', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'brun', 'Dos brun grisâtre, très vague sourcil blanc (à peine visible), poitrine mouchetée, barre alaire un peu plus claire que le reste du plumage. Fin bec noirâtre, jaune sur le dessous.'),
(346, 'Pipit rousseline', 'Anthus campestris', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'brun', 'Dos brun, sourcil blanc très marqué, ventre pâle dépourvu de taches noires (contrairement à la plupart des pipits). Bec fin.'),
(347, 'Pipit spioncelle', 'Anthus spinoletta', 'PASSERIFORMES: Motacillidae', 'Passereaux', 'brun', 'Dos brun, ventre pâle légèrement moucheté, sourcil blanc. Bec fin.'),
(348, 'Plongeon à bec blanc', 'Gavia adamsii', 'GAVIIFORMES: Gaviidae', 'Plongeons', 'noir', 'En plumage nuptial, dos noir à écailles blanche, tête noire, rayures noir et blanc sur le cou. En hiver, le plumage est plus gris, les joues et le cou sont blancs. Le bec est blanc jaunâtre.'),
(349, 'Plongeon arctique', 'Gavia arctica', 'GAVIIFORMES: Gaviidae', 'Plongeons', 'noir', 'En plumage nuptial, tête grise, cou noir sur le devant, strié noir et blanc sur les côtés. Gorge striée noir et blanc, damier noir et blanc sur le dos. Bec noir, oeil rouge. Gris en hiver, avec le cou et les joues blanches.'),
(350, 'Plongeon catmarin', 'Gavia stellata', 'GAVIIFORMES: Gaviidae', 'Plongeons', 'gris', 'Dos gris sombre, tête grise clair, stries noires et blanches sur la nuque, tache rouge sur le cou. Bec noir. En hiver, gris avec le cou blanc.'),
(351, 'Plongeon imbrin', 'Gavia immer', 'GAVIIFORMES: Gaviidae', 'Plongeons', 'noir', 'En plumage nuptial, tête noire, collier de stries noires et blanches sur le cou, damier noir et blanc sur le dos. Gris en plumage hivernal, avec le cou blanc. Bec noir, oeil rouge.'),
(352, 'Pluvier argenté', 'Pluvialis squatarola', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'gris', 'En plumage nuptial, dos gris moucheté de blanc, tête plutôt blanche, joues gorge et ventre noirs séparé du dos gris par une bande blanche. Bec noir. La partie noire devient blanche en hiver, et le dos devient plus terne.'),
(353, 'Pluvier doré', 'Pluvialis apricaria', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'brun', 'Joues, gorge et ventre noirs, séparés du dos brun moucheté de jaune par une bande blanche. Petit bec noir.'),
(354, 'Pluvier guignard', 'Charadrius morinellus', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'brun', 'Dos brun. Ventre roux. Demi collier blanc. Gorge grise. Joues et sourcils blancs. Calotte noire et bec noirâtre.'),
(355, 'Pouillot à grands sourcils', 'Phylloscopus inornatus', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'vert', 'Dos gris verdâtre. Barres blanches sur les ailes. Grands sourcils crème. Petit bec fin.'),
(356, 'Pouillot boréal', 'Phylloscopus borealis', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'vert', 'Dos et tête brun olivâtre. Ventre blanc sans tache. Grands sourcils blancs. Bec fin, noir dessus et jaune dessous.'),
(357, 'Pouillot brun', 'Phylloscopus fuscatus', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'brun', 'Dos et tête bruns. Ventre blanc sans tache. Grands sourcils blancs. Bec fin, noir dessus et jaune dessous. '),
(358, 'Pouillot de Bonelli', 'Phylloscopus bonelli', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'vert', 'Dos brun olivâtre. Ventre blanc. Sourcils blancs moins marqué que chez les pouillots brun et boréal. Bec fin.'),
(359, 'Pouillot fitis', 'Phylloscopus trochilus', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'gris', 'Dos et tête brun olivâtre. Ventre blanc sans tache. Sourcils crème. Bec fin, noir dessus et jaune dessous.'),
(360, 'Pouillot ibérique', 'Phylloscopus ibericus', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'brun', 'Dos brun olivâtre. Ventre blanc. Sourcils blancs moins marqué que chez les pouillots brun et boréal. Bec fin.'),
(361, 'Pouillot siffleur', 'Phylloscopus sibilatrix', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'vert', 'Tête et dos gris jaunâtre. Sourcils et joues jaunes. Ventre blanc. Bec fin.'),
(362, 'Pouillot véloce', 'Phylloscopus collybita', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'gris', 'Dos et tête grisâtres. Ventre blanc sans tache. Sourcils crème. Bec fin, noir dessus et jaune dessous. '),
(363, 'Pouillot verdâtre', 'Phylloscopus trochiloides', 'PASSERIFORMES: Phylloscopidae', 'Passereaux', 'vert', 'Dos olivâtre. Ventre blanc. Grand sourcil crème. Bec fin.'),
(364, 'Puffin cendré', 'Calonectris diomedea', 'PROCELLARIIFORMES: Procellariidae', 'Mouettes, goéland, fulmars', 'brun', 'Dos et tête brun gris. Ventre et dessous des ailes blancs. Bec jaune à bout noirâtre sur le dessus.'),
(365, 'Puffin des Anglais', 'Puffinus puffinus', 'PROCELLARIIFORMES: Procellariidae', 'Mouettes, goéland, fulmars', 'noir', 'Dos et tête noirs. Ventre et dessous des ailes blancs. Bec noir.'),
(366, 'Puffin fuligineux', 'Puffinus griseus', 'PROCELLARIIFORMES: Procellariidae', 'Mouettes, goéland, fulmars', 'brun', 'Entièrement brun. Bec noir.'),
(367, 'Puffin majeur', 'Puffinus gravis', 'PROCELLARIIFORMES: Procellariidae', 'Mouettes, goéland, fulmars', 'brun', 'Ailes et dos bruns à écailles. Calotte brune. Collier et dessous blancs. Bec noir. Marques brunes sur le ventre et sous les ailes.'),
(368, 'Puffin yelkouan', 'Puffinus yelkouan', 'PROCELLARIIFORMES: Procellariidae', 'Mouettes, goéland, fulmars', 'noir', 'Dos et tête bruns. Ventre et dessous des ailes blancs.'),
(369, 'Pygargue à queue blanche', 'Haliaeetus albicilla', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Corps brun, queue en losange et blanche. Bec et pattes jaunes. Le plumage des jeunes est plus foncé et s\'éclaircit au fil du temps.'),
(370, 'Râle d\'eau', 'Rallus aquaticus', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'brun', 'Dos brun moucheté de noir. Joues et gorge grises. Flancs et bas du ventre noirs rayés de blanc. Long bec orange et noirâtre. Oeil rouge. '),
(371, 'Râle des genêts', 'Crex crex', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'brun', 'Dos brun clair. Taches plus sombres formant un motif d\'écailles sur dos et le dessus de la tête. Gorge grisâtre. Bec épais rosé.'),
(372, 'Rémiz penduline', 'Remiz pendulinus', 'PASSERIFORMES: Remizidae', 'Passereaux', 'brun', 'Dos brun avec le bout des plumes noirâtre. Tête gris-blanc avec une tache noire autour de l\'oeil. Gorge blanchâtre, ventre brun très pâle. Bec fin.'),
(373, 'Roitelet à triple bandeau', 'Regulus ignicapilla', 'PASSERIFORMES: Regulidae', 'Passereaux', 'vert', 'Dos jaune olivâtre avec les ailes noirâtres, cou gris. Les yeux sont surmontés d\'une bande blanche qui passe au-dessus du bec. Sur le dessus de la tête, bande (parfois huppée) jaune chez la femelle, orange chez le mâle, entourée de bandes noires. Bande plus foncée sur l\'œil également. Dessous grisâtre. Bec fin noir.'),
(374, 'Roitelet huppé', 'Regulus regulus', 'PASSERIFORMES: Regulidae', 'Passereaux', 'vert', 'Dos olivâtre. Dessous blanc. Tête grise. Bande jaune cernée de noir sur la tête. Zone blanche autour de l\'oeil. Bec fin noir.'),
(375, 'Rollier d\'Europe', 'Coracias garrulus', 'CORACIIFORMES: Coraciidae', 'Autres', 'bleu', 'Dos roux. Bord des ailes noir. Tête et ventre bleus. Bec épais noir. Quelques plumes brunes autour du bec.'),
(376, 'Roselin cramoisi', 'Carpodacus erythrinus', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Dos brun. Ventre blanchâtre. En plumage nuptial, tête, gorge et croupion rouges. En dehors de la période nuptiale, brun olivâtre, comme la femelle. Bec épais. '),
(377, 'Roselin githagine', 'Bucanetes githagineus', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Dos gris brun, tête grise, bout des ailes noir. Bec rouge très épais.'),
(378, 'Rossignol philomèle', 'Luscinia megarhynchos', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Dos brun. Tête brune. Ventre blanc. Queue rousse assez longue. Bec fin.'),
(379, 'Rossignol progné', 'Luscinia luscinia', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Dos brun. Tête brune. Ventre blanc. Queue moins rousse que pour le rossignol philomèle. Bec fin.'),
(380, 'Rougegorge familier', 'Erithacus rubecula', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Vaste tache rouge s\'étendant du front au milieu du ventre. Dos brun. Bas du ventre blanchâtre. Bec fin noir.'),
(381, 'Rougequeue à front blanc', 'Phoenicurus phoenicurus', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Dos et tête gris, poitrine et queue rousses, front blanc, joues noires. La femelle n\'a pas de tache blanche et a le dos brun. Pattes et bec noirs.'),
(382, 'Rougequeue noir', 'Phoenicurus ochruros', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Corps gris-noir, croupion et queue roux, parfois taches blanches au niveau des ailes. Bec fin et pattes noirs.'),
(383, 'Rousserolle des buissons', 'Acrocephalus dumetorum', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dos gris brun. Dessous blanc. Vague sourcil blanc. Bec fin.'),
(384, 'Rousserolle effarvatte', 'Acrocephalus scirpaceus', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dos brun. Bord des ailes plus sombre. Tête brun clair. Dessous pâle. Petit bec fin.'),
(385, 'Rousserolle turdoïde', 'Acrocephalus arundinaceus', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dos brun. Tête brune. Gorge blanche. Ventre brun roux. Vague sourcil crème. Bec fin.'),
(386, 'Rousserolle verderolle', 'Acrocephalus palustris', 'PASSERIFORMES: Acrocephalidae', 'Passereaux', 'brun', 'Dos et tête olivâtres. Ventre blanchâtre. Très léger sourcil blanc. Bec fin.');
INSERT INTO `oiseaux` (`id`, `nom`, `nom_latin`, `ordre:famille`, `categorie`, `couleur`, `description`) VALUES
(387, 'Sarcelle d\'été', 'Anas querquedula', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'brun', 'Tête, cou et gorge bruns avec un large sourcil blanc qui descend sur la nuque. Flancs gris marbrés surmontés de plumes effilées rayées noir et blanc. Croupion moucheté. Bec noirâtre.'),
(388, 'Sarcelle d\'hiver', 'Anas crecca', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'gris', 'Tête rousse avec un bandeau vert sur l\'oeil. Gorge crème mouchetée. Flancs gris marbré coupés par une bande blanche. Dos gris. Croupion blanc. Miroir vert. Bec noir. La femelle est brune avec un miroir vert.'),
(389, 'Serin cini', 'Serinus serinus', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'jaune', 'Dos brun sombre strié de jaune. Croupion jaune. Marques jaunes autour de l\'oeil, de la joue et de la gorge. Poitrine rayée. Bec épais.'),
(390, 'Sittelle corse', 'Sitta whiteheadi', 'PASSERIFORMES: Sittidae', 'Passereaux', 'gris', 'Dos gris. Ventre beige. Calotte noire. Sourcils blancs. Bande noire sur l\'oeil. Bec fin noirâtre.'),
(391, 'Sittelle de Neumayer', 'Sitta neumayer', 'PASSERIFORMES: Sittidae', 'Passereaux', 'gris', 'Dos gris. Calotte grise. Bande noire sur l\'oeil. Joues et gorge blanches. Brunâtre autour des pattes. Bec fin et noirâtre.'),
(392, 'Sittelle torchepot', 'Sitta europaea', 'PASSERIFORMES: Sittidae', 'Passereaux', 'gris', 'Dos gris bleu. Bandeau noir sur l\'oeil. Ventre roux. Joues blanches.La sittelle torchepot est le seul oiseau capable de descendre les troncs d\'arbres la tête en bas. Bec fin noir.'),
(393, 'Sizerin blanchâtre', 'Carduelis hornemanni', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Dos et tête bruns. Ventre blanc légèrement strié de noir. Tache rouge sur le front. Légèrement plus pâle que le sizerin flammé. Bec épais jaune.'),
(394, 'Sizerin flammé', 'Carduelis flammea', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'brun', 'Dos brun. Tête brune. Ventre blanc légèrement strié de brun. Tache rouge sur le front. Bec épais jaunâtre.'),
(395, 'Spatule blanche', 'Platalea leucorodia', 'PELECANIFORMES: Threskiornithidae', 'Grands échassiers', 'blanc', 'Blanc avec quelques plumes rousses au niveau du cou. Longues aigrettes derrière la tête. Morceau de peau nue jaune orangée sous un long bec noir en forme de spatule. L\'extrémité du bec est jaune. Longues pattes noires.'),
(396, 'Sterne arctique', 'Sterna paradisaea', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Ailes gris pâle sur le dessus, blanches en dessous. Corps gris très pâle. Collier blanc. Tête noire. Bec fin orange. Pattes orange.'),
(397, 'Sterne caspienne', 'Hydroprogne caspia', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Ailes gris pâle sur le dessus, blanches en dessous. Corps gris très pâle. Joues et gorge blanches. Tête noire. Puissant bec rouge orange à bout noirâtre. Pattes noires.'),
(398, 'Sterne caugek', 'Thalasseus sandvicensis', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Dessus gris très pâle. Dessous blanc. Calotte noire. Bec fin noir à bout jaune. Pattes noires.'),
(399, 'Sterne de Dougall', 'Sterna dougallii', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Dessus gris très pâle. Dessous blanc. Calotte noire. Bec fin orange à bout noir. Pattes oranges.'),
(400, 'Sterne hansel', 'Gelochelidon nilotica', 'CHARADRIIFORMES: Laridae', 'Sternes', 'blanc', 'Dessus gris très pâle. Dessous blanc. Calotte noire s\'étendant sur la nuque. Puissant bec noir. Pattes noires.'),
(401, 'Sterne naine', 'Sternula albifrons', 'CHARADRIIFORMES: Laridae', 'Sternes', 'blanc', 'Dessus gris très pâle. Dessous blanc. Calotte noire avec une tache blanche sur le front. Bec fin jaune à bout noirâtre. Pattes orange.'),
(402, 'Sterne pierregarin', 'Sterna hirundo', 'CHARADRIIFORMES: Laridae', 'Sternes', 'gris', 'Ailes gris pâle sur le dessus, blanches en dessous. Corps gris très pâle. Collier blanc. Calotte noire descendant sur la nuque. Pattes orange. Se distingue de la sterne arctique par un bec plus épais à bout noir.'),
(403, 'Tadorne casarca', 'Tadorna ferruginea', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'rouge/orange', 'Tête brun pâle. Corps entièrement roux. Bout des ailes noir. Petit collier noir pour le mâle. Miroir vert et bec noir.'),
(404, 'Tadorne de Belon', 'Tadorna tadorna', 'ANSERIFORMES: Anatidae', 'Cygnes, oies, canards', 'blanc', 'Corps blanc sauf la tête vert foncé, le collier roux et le bout des ailes noirs. Le mâle a un renflement au-dessus du bec rouge. Pattes rose.'),
(405, 'Talève sultane', 'Porphyrio porphyrio', 'GRUIFORMES: Rallidae', 'Râles, marouettes, poules d\'eau', 'noir', 'Tête, cou et gorge gris bleu sombre. Ailes noires, croupion blanc. Bec épais rouge avec une plaque frontale rouge également.'),
(406, 'Tarier des prés', 'Saxicola rubetra', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Dos brun et noir avec un motif d\'écailles. Tête noire striée de brun sur le dessus. Sourcil blanc. Bavette blanche. Poitrine rousse. Bec fin noir.'),
(407, 'Tarier pâtre', 'Saxicola rubicola', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Tête, gorge et dos brun noir. Blanc au niveau du cou et de l\'épaule. Poitrine rousse. Bande blanche sur l\'aile. Bec fin noir. Pattes noires.'),
(408, 'Tarin des aulnes', 'Carduelis spinus', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'jaune', 'Dos, tête et gorge jaunes. Front noir. Ailes et queue barrées de noir. Bec épais.'),
(409, 'Tétras lyre', 'Lyrurus tetrix', 'GALLIFORMES: Phasianidae', 'Gallinacés', 'noir', 'Entièrement noir à l\'exception de la caroncule rouge et des plumes blanches sous la queue. Femelle brune mouchetée.'),
(410, 'Tichodrome échelette', 'Tichodroma muraria', 'PASSERIFORMES: Tichodromidae', 'Passereaux', 'gris', 'Corps gris. Ailes noires tachées de rouge et de blanc. Bec long, fin et noir.'),
(411, 'Torcol fourmilier', 'Jynx torquilla', 'PICIFORMES: Picidae', 'Pics', 'gris', 'Globalement gris brun avec un plumage ressemblant à de l\'écorce d\'arbre. Bec épais.'),
(412, 'Tournepierre à collier', 'Arenaria interpres', 'CHARADRIIFORMES: Scolopacidae', 'Limicoles', 'brun', 'Dos brun, noir et roux. Collier noir et blanc. Gorge noire. Ventre blanc. Calotte blanche en été. En hiver, tête et gorge brunes. Pattes orange. Bec fin noir.'),
(413, 'Tourterelle des bois', 'Streptopelia turtur', 'COLUMBIFORMES: Columbidae', 'Pigeons et tourterelles', 'brun', 'Tête, ventre et dos gris. Ailes avec motif d\'écaille noir bordé de brun. Tache noir et blanc sur le cou. Bec noir. Tour de l\'oeil rouge.'),
(414, 'Tourterelle turque', 'Streptopelia decaocto', 'COLUMBIFORMES: Columbidae', 'Pigeons et tourterelles', 'gris', 'Grise avec le bout des ailes noir. Poitrine légèrement rosée. Marque noire au niveau du cou. Bec et pattes noirs.'),
(415, 'Traquet isabelle', 'Oenanthe isabellina', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Tête et dos bruns. Bord des ailes plus sombres. Sourcil blanc. Trait noir entre l\'oeil et le bec. Bavette blanche. Gorge brunâtre. Ventre blanc. Pattes noires, bec fin et noir.'),
(416, 'Traquet motteux', 'Oenanthe oenanthe', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'gris', 'Tête et dos gris. Bord des ailes noir. Bande noire sur l\'oeil surmontée d\'un sourcil blanc. Bavette légèrement rousse. Ventre blanc. Bec fin noir. Pattes noires. Chez la femelle, dos et tête sont bruns. Pas de bande noire sur l\'oeil ni de gorge rousse.'),
(417, 'Traquet oreillard', 'Oenanthe hispanica', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'brun', 'Tête, dos, cou, ventre et queue blancs. Ailes noires. Bandeau noir sur l\'oeil. Petit bec fin. Pattes noires. Queue noire et blanche.'),
(418, 'Traquet pie', 'Oenanthe pleschanka', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Calotte et nuque blanches. Ventre blanc. Joues, gorge et dos noirs. Queue blanche avec un T noir. Bec fin noir. Pattes noires.'),
(419, 'Traquet rieur', 'Oenanthe leucura', 'PASSERIFORMES: Muscicapidae', 'Passereaux', 'noir', 'Entièrement noir à l\'exception de la calotte grise et du dessous de la queue blanc. Bec fin noir. Pattes noires.'),
(420, 'Troglodyte mignon', 'Troglodytes troglodytes', 'PASSERIFORMES: Troglodytidae', 'Passereaux', 'brun', 'Dessus brun roux, finement strié de noir. Tête et ventre brun plus pâle. Sourcil blanc. Quelques plumes fines à damier noir et blanc au bord des ailes. Queue souvent relevée. Petit bec fin.'),
(421, 'Turnix d\'Andalousie', 'Turnix sylvaticus', 'CHARADRIIFORMES: Turnicidae', 'Autres', 'brun', 'Dos grisâtre moucheté de blanc, noir et brun. Dessous roux. Tête grise mouchetée de blanc. Bec épais grisâtre. '),
(422, 'Vanneau éperonné', 'Vanellus spinosus', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'brun', 'Dos brun. Calotte, ventre, épaules et queue noirs. Bande noire entre le bec et le ventre. Joue et cou blancs. Bas du ventre blanc. Bec fin noir.'),
(423, 'Vanneau huppé', 'Vanellus vanellus', 'CHARADRIIFORMES: Charadriidae', 'Limicoles', 'noir', 'Calotte noire huppée. Gorge noire. Joue et tour de l\'oeil marbré noir et blanc. Nuque et croupion brunâtres. Ventre blanc. Dos à reflets verts. Bec noir et fin. Produit un effet de clignotement en vol.'),
(424, 'Vautour fauve', 'Gyps fulvus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Brun à l\'exception du bord des ailes et de la queue noirs. Tête et cou gris et nus. Bec crochu.'),
(425, 'Vautour moine', 'Aegypius monachus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'brun', 'Corps brun sombre. Tête nue. Bec crochu.'),
(426, 'Vautour percnoptère', 'Neophron percnopterus', 'ACCIPITRIFORMES: Accipitridae', 'Rapaces', 'blanc', 'Blanc, avec le dessous des ailes partiellement noir. Face nue jaune. Bec crochu.'),
(427, 'Venturon corse', 'Serinus corsicanus', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'vert', 'Dos brun. Tête grisâtre, sauf le front et les joues jaunes. Bout des ailes jaunes et noir. Dessous des ailes jaunes. Bec épais.'),
(428, 'Venturon montagnard', 'Serinus citrinella', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'vert', 'Dos, dessous et face jaunes. Nuque grisâtre. Collier blanchâtre. Bout des ailes noir. Bec épais.'),
(429, 'Verdier d\'Europe', 'Chloris chloris', 'PASSERIFORMES: Fringillidae', 'Passereaux', 'vert', 'Dos jaune verdâtre. Tête grise et jaune. Dessous jaune. Ailes et queue grises et noires. Croupion jaune. Bec épais.');
